PGPASSWORD="autojobr_2025_secure" psql -h localhost -U autojobr_user -d autojobr << 'EOF'
-- Drop all tables in correct order (respecting foreign keys)
DROP TABLE IF EXISTS virtual_interview_sessions CASCADE;
