      ADD COLUMN IF NOT EXISTS premium_trial_end_date TIMESTAMP DEFAULT NOW() + INTERVAL '30 days',
      ADD COLUMN IF NOT EXISTS has_used_premium_trial BOOLEAN DEFAULT false;
    `);

