  isJobApplicationPage() {
    const url = window.location.href.toLowerCase();
    const hostname = window.location.hostname.toLowerCase();

    // LinkedIn specific detection - avoid feeds, home, search pages
    if (hostname.includes('linkedin.com')) {
      // Must be jobs page AND have easy apply or application form
      const isJobsPage = url.includes('/jobs/view/') || url.includes('/jobs/collections/');
      const hasEasyApply = document.querySelector('[data-test-modal="jobs-easy-apply-modal"], .jobs-easy-apply-content, .jobs-apply-button');
      const isFeedPage = url.includes('/feed/') || url.includes('/mynetwork/') || url === 'https://www.linkedin.com/';

      return isJobsPage && hasEasyApply && !isFeedPage;
    }

    // Workday specific detection
    if (hostname.includes('myworkdayjobs.com')) {
      return url.includes('/job/') && document.querySelector('form[data-automation-id="jobApplicationForm"], .css-1x9zq2f');
    }

    // Indeed specific detection
    if (hostname.includes('indeed.com')) {
      return url.includes('/viewjob') && document.querySelector('.indeed-apply-button, .ia-IndeedApplyButton');
    }

    // Generic detection for other sites
    const pageText = document.body.textContent.toLowerCase();
    const hasStrictJobForm = document.querySelectorAll('input[type="file"][accept*="pdf"], textarea[name*="cover"], input[name*="resume"]').length > 0;
    const hasApplyButton = document.querySelector('[class*="apply"], [id*="apply"], button[data-test*="apply"]');

    return hasStrictJobForm && hasApplyButton;
  }

  // Setup automatic job analysis when new pages load
  setupAutoAnalysis() {
    // Analyze current page if it's a job page
    if (this.isJobApplicationPage()) {
      setTimeout(() => this.performAutoAnalysis(), 2000);
    }

    // Watch for URL changes (SPA navigation)
    let lastUrl = window.location.href;
    const urlObserver = new MutationObserver(() => {
      if (window.location.href !== lastUrl) {
        lastUrl = window.location.href;
        if (this.isJobApplicationPage()) {
          setTimeout(() => this.performAutoAnalysis(), 3000);
        }
      }
    });

    urlObserver.observe(document.body, {
      childList: true,
      subtree: true
    });

    this.observers.push(urlObserver);
  }

  async performAutoAnalysis() {
    try {
      const jobData = this.extractJobData();
      if (!jobData || !jobData.title) {
        console.log('No job data found for analysis');
        return;
      }

      // Get user profile first
      const profile = await this.getUserProfile();
      if (!profile) {
        console.log('User not authenticated - skipping auto analysis');
        return;
      }

      // Perform enhanced job analysis
      const analysis = await this.analyzeJobWithAPI(jobData, profile);
      if (analysis) {
        // Update floating button with analysis results
        this.updateFloatingButtonWithAnalysis(analysis);
        console.log('Auto-analysis completed:', analysis);
      }
    } catch (error) {
      console.error('Auto-analysis failed:', error);
    }
  }

  extractJobData() {
    const url = window.location.href;
    const hostname = window.location.hostname.toLowerCase();

    let jobData = {
      title: '',
      company: '',
      description: '',
      location: '',
      salary: '',
      url: url
    };

    // LinkedIn job extraction
    if (hostname.includes('linkedin.com')) {
      jobData.title = document.querySelector('.job-details-jobs-unified-top-card__job-title, .job-title')?.textContent?.trim() || '';
      jobData.company = document.querySelector('.job-details-jobs-unified-top-card__company-name, .company-name')?.textContent?.trim() || '';
      jobData.location = document.querySelector('.job-details-jobs-unified-top-card__bullet, .job-location')?.textContent?.trim() || '';
      jobData.description = document.querySelector('.job-details__description-text, .job-view-description')?.textContent?.trim() || '';
    }

    // Workday job extraction
    else if (hostname.includes('myworkdayjobs.com')) {
      jobData.title = document.querySelector('[data-automation-id="jobPostingHeader"], .css-1id67r3')?.textContent?.trim() || '';
      jobData.company = document.querySelector('[data-automation-id="jobPostingCompany"], .css-1x9zq2f')?.textContent?.trim() || '';
      jobData.location = document.querySelector('[data-automation-id="jobPostingLocation"]')?.textContent?.trim() || '';
      jobData.description = document.querySelector('[data-automation-id="jobPostingDescription"]')?.textContent?.trim() || '';
    }

    // Indeed job extraction
    else if (hostname.includes('indeed.com')) {
      jobData.title = document.querySelector('[data-jk] h1, .jobsearch-JobInfoHeader-title')?.textContent?.trim() || '';
      jobData.company = document.querySelector('[data-testid="inlineHeader-companyName"]')?.textContent?.trim() || '';
      jobData.location = document.querySelector('[data-testid="job-location"]')?.textContent?.trim() || '';
      jobData.description = document.querySelector('#jobDescriptionText, .jobsearch-jobDescriptionText')?.textContent?.trim() || '';
    }

    // Generic extraction for other sites
    else {
      jobData.title = document.querySelector('h1, .job-title, [class*="title"]')?.textContent?.trim() || '';
      jobData.company = document.querySelector('.company, [class*="company"]')?.textContent?.trim() || '';
      jobData.description = document.querySelector('.description, .job-description, [class*="description"]')?.textContent?.trim() || '';
    }

    return jobData.title ? jobData : null;
  }

  async analyzeJobWithAPI(jobData, userProfile) {
    try {
      const apiUrl = await this.getApiUrl();
      const response = await fetch(`${apiUrl}/api/analyze-job-match`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify({
          jobData: {
            title: jobData.title,
            company: jobData.company,
            description: jobData.description,
            requirements: jobData.description,
            qualifications: jobData.description,
            benefits: jobData.description,
            location: jobData.location,
            salary: jobData.salary,
            url: jobData.url
          },
          userProfile
        })
      });

      if (!response.ok) {
        throw new Error(`Analysis failed: ${response.status}`);
      }

      const result = await response.json();
      return result;
    } catch (error) {
      console.error('API job analysis failed:', error);
      return null;
    }
  }

  updateFloatingButtonWithAnalysis(analysis) {
    const button = document.getElementById('autojobr-floating-button');
    if (!button) return;

    const score = analysis.matchScore || analysis.analysis?.matchScore || 0;
    const scoreText = `${Math.round(score)}%`;

    // Update button with score and click handler to open extension popup
    button.innerHTML = `
      <div style="
        position: fixed;
        bottom: 20px;
        right: 20px;
        width: 60px;
        height: 60px;
        background: linear-gradient(135deg, ${this.getScoreColor(score)} 0%, ${this.getScoreColor(score)}dd 100%);
        border-radius: 50%;
        box-shadow: 0 4px 20px rgba(102, 126, 234, 0.4);
        cursor: pointer;
        display: flex;
        align-items: center;
        justify-content: center;
        z-index: 10000;
        transition: all 0.3s ease;
        animation: pulse 2s infinite;
      " title="Job Match: ${scoreText} - Click to open AutoJobr extension">
        <span style="color: white; font-weight: bold; font-size: 12px; text-align: center;">
          ${scoreText}
        </span>
      </div>
      <style>
        @keyframes pulse {
          0% { box-shadow: 0 4px 20px rgba(102, 126, 234, 0.4); }
          50% { box-shadow: 0 4px 20px rgba(102, 126, 234, 0.8); }
          100% { box-shadow: 0 4px 20px rgba(102, 126, 234, 0.4); }
        }
        #autojobr-floating-button:hover > div {
          transform: scale(1.1);
          box-shadow: 0 6px 25px rgba(102, 126, 234, 0.6);
        }
      </style>
    `;

    // Add click handler to open extension popup
    button.onclick = () => {
      // Try to open popup, fallback to notification
      chrome.runtime.sendMessage({ action: 'openPopup' }, (response) => {
        if (!response?.success) {
          // Show notification if popup couldn't be opened
          const notification = document.createElement('div');
          notification.style.cssText = `
            position: fixed;
            bottom: 90px;
            right: 20px;
            background: rgba(0,0,0,0.9);
            color: white;
            padding: 8px 12px;
            border-radius: 6px;
            font-size: 12px;
            z-index: 10001;
            animation: fadeInUp 0.3s ease;
          `;
          notification.textContent = 'Click the AutoJobr extension icon in your toolbar to view details';
          document.body.appendChild(notification);

          // Remove notification after 3 seconds
          setTimeout(() => notification.remove(), 3000);
        }
      });
    };

    // Store analysis data for popup use
    this.currentAnalysis = analysis;
  }

  getScoreColor(score) {
    if (score >= 80) return '#22c55e';
    if (score >= 60) return '#f59e0b';
    if (score >= 40) return '#f97316';
    return '#ef4444';
  }

  async getUserProfile() {
    try {
      const apiUrl = await this.getApiUrl();
      const response = await fetch(`${apiUrl}/api/extension/profile`, {
        method: 'GET',
        credentials: 'include'
      });

      if (response.ok) {
        return await response.json();
      }
      return null;
    } catch (error) {
      console.error('Failed to get user profile:', error);
      return null;
    }
  }

  async getApiUrl() {
    return new Promise((resolve) => {
      chrome.runtime.sendMessage({ action: 'getApiUrl' }, (response) => {
        resolve(response?.apiUrl || 'https://474e72d5-d02a-4881-a1b1-207472132974-00-13rhdq6o0h8j1.worf.replit.dev');
      });
    });
  }

  // Cleanup method
  destroy() {
    this.observers.forEach(observer => observer.disconnect());
    this.observers = [];

    const overlay = document.getElementById('autojobr-overlay');
    const button = document.getElementById('autojobr-floating-button');
    if (overlay) overlay.remove();
    if (button) button.remove();
  }
}

// Add message listener for getting current analysis data
chrome.runtime.onMessage.addListener((message, sender, sendResponse) => {
  if (message.action === 'getCurrentAnalysis') {
    const extension = window.autojobrExtension;
    if (extension && extension.currentAnalysis) {
      sendResponse({
        success: true,
        analysis: extension.currentAnalysis,
        jobData: extension.extractJobData()
      });
    } else {
      sendResponse({ success: false });
    }
  }
  return true;
});

// Initialize content script
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', () => {
    const extension = new AutoJobrContentScript();
    window.autojobrExtension = extension; // Store reference for message handling
    // Show floating button on job application pages after a delay
    setTimeout(() => extension.createFloatingButton(), 3000);
  });
} else {
  const extension = new AutoJobrContentScript();
  window.autojobrExtension = extension; // Store reference for message handling
  // Show floating button on job application pages after a delay  
  setTimeout(() => extension.createFloatingButton(), 3000);
}

extension/popup-styles.css.content:
/* Enhanced AutoJobr Extension Styles v2.0 */

/* CSS Variables for theming */
:root {
  --primary-color: #3b82f6;
  --primary-hover: #1d4ed8;
  --success-color: #22c55e;
  --success-hover: #16a34a;
  --warning-color: #f59e0b;
  --error-color: #ef4444;
  --text-primary: #1f2937;
  --text-secondary: #6b7280;
  --bg-primary: #ffffff;
  --bg-secondary: #f8fafc;
  --border-color: #e5e7eb;
  --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
  --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1);
  --radius-sm: 4px;
  --radius-md: 8px;
  --radius-lg: 12px;
  --radius-xl: 16px;
}

/* Base styles */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

/* AutoJobr Widget Styles */
.autojobr-widget {
  position: fixed;
  top: 20px;
  right: 20px;
  width: 360px;
  max-width: 90vw;
  background: var(--bg-primary);
  border-radius: var(--radius-xl);
  box-shadow: var(--shadow-xl);
  border: 1px solid var(--border-color);
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
  z-index: 10000;
  overflow: hidden;
  backdrop-filter: blur(20px);
  animation: slideInRight 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

@keyframes slideInRight {
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.autojobr-header {
  background: linear-gradient(135deg, var(--primary-color) 0%, #1e40af 100%);
  color: white;
  padding: 16px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  cursor: grab;
  user-select: none;
}

.autojobr-header:active {
  cursor: grabbing;
}

.autojobr-logo {
  display: flex;
  align-items: center;
  gap: 10px;
  font-weight: 700;
  font-size: 16px;
}

.autojobr-icon {
  width: 28px;
  height: 28px;
  background: linear-gradient(135deg, #00d4ff, #0ea5e9);
  border-radius: var(--radius-md);
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
  font-size: 14px;
  box-shadow: 0 4px 12px rgba(0, 212, 255, 0.3);
}

.autojobr-controls {
  display: flex;
  gap: 8px;
}

.autojobr-minimize,
.autojobr-close {
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  width: 24px;
  height: 24px;
  border-radius: var(--radius-sm);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 14px;
  font-weight: bold;
  transition: all 0.2s ease;
}

.autojobr-minimize:hover,
.autojobr-close:hover {
  background: rgba(255, 255, 255, 0.3);
  transform: scale(1.1);
}

.autojobr-content {
  padding: 20px;
}

.autojobr-status {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 12px 16px;
  background: linear-gradient(135deg, #f0fdf4, #dcfce7);
  border: 1px solid var(--success-color);
  border-radius: var(--radius-lg);
  margin-bottom: 16px;
  font-size: 13px;
  position: relative;
  overflow: hidden;
}

.autojobr-status::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, var(--success-color), #16a34a);
}

.status-icon {
  font-size: 16px;
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.1); }
}

.status-text {
  color: #166534;
  font-weight: 500;
  flex: 1;
}

.status-progress {
  width: 100%;
  height: 4px;
  background: rgba(34, 197, 94, 0.2);
  border-radius: 2px;
  overflow: hidden;
  margin-top: 8px;
}

.progress-bar {
  height: 100%;
  background: linear-gradient(90deg, var(--success-color), #16a34a);
  width: 0%;
  transition: width 0.3s ease;
  position: relative;
}

.progress-bar::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
  animation: shimmer 1.5s infinite;
}

@keyframes shimmer {
  0% { transform: translateX(-100%); }
  100% { transform: translateX(100%); }
}

.autojobr-job-info {
  background: linear-gradient(135deg, #f0f9ff, #e0f2fe);
  border: 1px solid #0ea5e9;
  border-radius: var(--radius-lg);
  padding: 16px;
  margin-bottom: 16px;
  position: relative;
  overflow: hidden;
}

.autojobr-job-info::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, #0ea5e9, #0284c7);
}

.job-title {
  font-weight: 600;
  color: #0c4a6e;
  font-size: 14px;
  margin-bottom: 4px;
}

.job-company {
  color: #0369a1;
  font-size: 12px;
  margin-bottom: 8px;
}

.job-match {
  font-size: 12px;
}

.match-score {
  padding: 4px 8px;
  border-radius: var(--radius-sm);
  font-weight: 600;
  display: inline-block;
}

.match-score.excellent {
  background: #dcfce7;
  color: #166534;
}

.match-score.good {
  background: #fef3c7;
  color: #92400e;
}

.match-score.fair {
  background: #fed7aa;
  color: #9a3412;
}

.match-score.poor {
  background: #fee2e2;
  color: #991b1b;
}

.autojobr-actions {
  margin-bottom: 16px;
}

.autojobr-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px 16px;
  border: 2px solid var(--border-color);
  border-radius: var(--radius-lg);
  background: var(--bg-primary);
  color: var(--text-primary);
  font-size: 13px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  text-align: left;
  width: 100%;
  margin-bottom: 8px;
  position: relative;
  overflow: hidden;
}

.autojobr-btn::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
  transition: left 0.5s;
}

.autojobr-btn:hover::before {
  left: 100%;
}

.autojobr-btn:hover {
  transform: translateY(-2px);
  box-shadow: var(--shadow-lg);
}

.autojobr-btn.primary {
  background: linear-gradient(135deg, var(--primary-color), var(--primary-hover));
  color: white;
  border-color: var(--primary-color);
}

.autojobr-btn.primary:hover {
  background: linear-gradient(135deg, var(--primary-hover), #1e40af);
  box-shadow: 0 8px 25px rgba(59, 130, 246, 0.3);
}

.autojobr-btn.secondary {
  background: var(--bg-secondary);
  border-color: var(--border-color);
}

.autojobr-btn.secondary:hover {
  background: #f1f5f9;
  border-color: #cbd5e1;
}

.autojobr-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  transform: none;
}

.autojobr-btn:disabled:hover {
  transform: none;
  box-shadow: none;
}

.btn-icon {
  font-size: 16px;
  flex-shrink: 0;
}

.btn-text {
  flex: 1;
}

.btn-shortcut {
  font-size: 10px;
  opacity: 0.7;
  background: rgba(255, 255, 255, 0.2);
  padding: 2px 6px;
  border-radius: var(--radius-sm);
}

.action-row {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 8px;
}

.action-row .autojobr-btn {
  margin-bottom: 0;
  padding: 10px 8px;
  font-size: 11px;
  flex-direction: column;
  text-align: center;
  gap: 4px;
}

.autojobr-features {
  border-top: 1px solid var(--border-color);
  padding-top: 16px;
  margin-bottom: 16px;
}

.feature-toggle {
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 8px;
}

.feature-toggle input[type="checkbox"] {
  width: 16px;
  height: 16px;
  accent-color: var(--primary-color);
}

.feature-toggle label {
  font-size: 12px;
  color: var(--text-secondary);
  cursor: pointer;
}

.autojobr-stats {
  background: var(--bg-secondary);
  border-radius: var(--radius-lg);
  padding: 12px;
  border: 1px solid var(--border-color);
}

.stat-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 4px;
  font-size: 12px;
}

.stat-item:last-child {
  margin-bottom: 0;
}

.stat-label {
  color: var(--text-secondary);
}

.stat-value {
  font-weight: 600;
  color: var(--text-primary);
}

/* Field feedback indicators */
.autojobr-field-indicator {
  position: fixed;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 12px;
  font-weight: bold;
  z-index: 10001;
  pointer-events: none;
  animation: fadeInScale 0.3s ease-out;
}

@keyframes fadeInScale {
  from {
    opacity: 0;
    transform: scale(0.5);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.autojobr-field-indicator.success {
  background: var(--success-color);
  color: white;
  box-shadow: 0 4px 12px rgba(34, 197, 94, 0.3);
}

.autojobr-field-indicator.error {
  background: var(--error-color);
  color: white;
  box-shadow: 0 4px 12px rgba(239, 68, 68, 0.3);
}

/* Notification styles */
.autojobr-notification {
  position: fixed;
  top: 20px;
  right: 20px;
  max-width: 320px;
  padding: 12px 16px;
  border-radius: var(--radius-lg);
  font-size: 14px;
  font-weight: 500;
  box-shadow: var(--shadow-xl);
  z-index: 10002;
  backdrop-filter: blur(10px);
  animation: slideInRight 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.autojobr-notification.success {
  background: var(--success-color);
  color: white;
}

.autojobr-notification.error {
  background: var(--error-color);
  color: white;
}

.autojobr-notification.info {
  background: var(--primary-color);
  color: white;
}

/* Responsive design */
@media (max-width: 480px) {
  .autojobr-widget {
    width: 320px;
    right: 10px;
    top: 10px;
  }

  .autojobr-content {
    padding: 16px;
  }

  .action-row {
    grid-template-columns: 1fr 1fr;
  }

  .action-row .autojobr-btn:last-child {
    grid-column: 1 / -1;
  }
}

/* Dark mode support */
@media (prefers-color-scheme: dark) {
  :root {
    --text-primary: #f9fafb;
    --text-secondary: #d1d5db;
    --bg-primary: #1f2937;
    --bg-secondary: #374151;
    --border-color: #4b5563;
  }

  .autojobr-widget {
    background: var(--bg-primary);
    border-color: var(--border-color);
  }

  .autojobr-btn.secondary {
    background: var(--bg-secondary);
    color: var(--text-primary);
  }

  .autojobr-stats {
    background: var(--bg-secondary);
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .autojobr-widget {
    border-width: 2px;
  }

  .autojobr-btn {
    border-width: 2px;
  }

  .autojobr-status,
  .autojobr-job-info {
    border-width: 2px;
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  .autojobr-widget,
  .autojobr-btn,
  .autojobr-field-indicator,
  .autojobr-notification {
    animation: none;
  }

  .status-icon {
    animation: none;
  }

  .progress-bar::after {
    animation: none;
  }

  .autojobr-btn::before {
    transition: none;
  }
}

/* Focus styles for accessibility */
.autojobr-btn:focus,
.autojobr-minimize:focus,
.autojobr-close:focus,
.feature-toggle input:focus {
  outline: 2px solid var(--primary-color);
  outline-offset: 2px;
}

/* Print styles */
@media print {
  .autojobr-widget,
  .autojobr-notification,
  .autojobr-field-indicator {
    display: none !important;
  }
}

