server/adminFixService.ts.content:
import { db } from "./db.js";
import { users, companyEmailVerifications } from "@shared/schema";
import { eq } from "drizzle-orm";
import { storage } from "./storage.js";

export class AdminFixService {

  // Fix user type issues for current user
  async fixCurrentUserToRecruiter(userEmail: string): Promise<boolean> {
    try {
      // Get user from database
      const existingUser = await storage.getUserByEmail(userEmail);
      if (!existingUser) {
        console.error('User not found:', userEmail);
        return false;
      }

      // Update user to recruiter
      await storage.upsertUser({
        ...existingUser,
        userType: "recruiter",
        emailVerified: true,
        companyName: existingUser.companyName || "Cyclelove Company",
        availableRoles: "job_seeker,recruiter",
        // currentRole will be automatically set to match userType
      });

      // Create company verification record if not exists
      try {
        await db.insert(companyEmailVerifications).values({
          userId: existingUser.id,
          email: existingUser.email,
          companyName: existingUser.companyName || "Cyclelove Company",
          companyWebsite: `https://${existingUser.email.split('@')[1]}`,
          verificationToken: `admin-fix-${Date.now()}`,
          isVerified: true,
          verifiedAt: new Date(),
          expiresAt: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000),
        });
      } catch (insertError) {
        // Record might exist, that's okay
        console.log('Company verification record already exists');
      }

      console.log(`âœ… Successfully upgraded ${userEmail} to recruiter status`);
      return true;
    } catch (error) {
      console.error('Error fixing user type:', error);
      return false;
    }
  }

  // Emergency endpoint to fix session issues
  async refreshUserSession(req: any, userEmail: string): Promise<boolean> {
    try {
      const user = await storage.getUserByEmail(userEmail);
      if (!user) {
        return false;
      }

      // Update session
      req.session.user = {
        id: user.id,
        email: user.email,
        userType: user.userType,
        firstName: user.firstName,
        lastName: user.lastName,
        companyName: user.companyName
      };

      // Save session
      return new Promise((resolve) => {
        req.session.save((err: any) => {
          if (err) {
            console.error('Session save error:', err);
            resolve(false);
          } else {
            console.log('âœ… Session refreshed successfully');
            resolve(true);
          }
        });
      });
    } catch (error) {
      console.error('Error refreshing session:', error);
      return false;
    }
  }
}

export const adminFixService = new AdminFixService();

server/aiDetectionService.ts.content:
import Groq from 'groq-sdk';

interface AIDetectionResult {
  isAIGenerated: boolean;
  confidence: number; // 0-100
  indicators: string[];
  humanScore: number; // 0-100, higher = more human-like
  reasoning: string;
}

interface ResponseAnalysis {
  originalAnalysis: any;
  aiDetection: AIDetectionResult;
  finalScore: number; // Adjusted score considering AI usage
  partialResultsOnly: boolean; // Flag for recruiters
}

export class AIDetectionService {
  private groq: Groq;

  constructor() {
    if (!process.env.GROQ_API_KEY) {
      console.warn("GROQ_API_KEY not found - AI detection will use fallback mode");
      this.groq = null as any; // Will use fallback detection
    } else {
      this.groq = new Groq({ apiKey: process.env.GROQ_API_KEY });
    }
  }

  async detectAIUsage(userResponse: string, questionContext?: string): Promise<AIDetectionResult> {
    // Quick checks for obvious AI patterns
    const quickIndicators = this.performQuickChecks(userResponse);

    if (quickIndicators.isObvious) {
      return {
        isAIGenerated: true,
        confidence: 95,
        indicators: quickIndicators.indicators,
        humanScore: 5,
        reasoning: "Contains obvious AI-generated patterns"
      };
    }

    // Use Groq for detailed analysis with minimal tokens (if available)
    if (!this.groq) {
      // Fallback to pattern-based detection only
      return {
        isAIGenerated: false,
        confidence: 30,
        indicators: ['API not available - pattern analysis only'],
        humanScore: 70,
        reasoning: 'Basic pattern analysis used (AI service unavailable)'
      };
    }

    try {
      const prompt = `Analyze if this response was AI-generated. Be concise.

Response: "${userResponse}"
${questionContext ? `Question: "${questionContext}"` : ''}

Check for:
- Unnatural phrasing/structure
- Generic AI-style responses
- Overly perfect grammar
- Typical AI patterns

Return JSON: {"aiGenerated": boolean, "confidence": 0-100, "humanScore": 0-100, "indicators": ["reason1", "reason2"], "reasoning": "brief explanation"}`;

      const response = await this.groq.chat.completions.create({
        messages: [{ role: 'user', content: prompt }],
        model: 'llama-3.1-8b-instant', // Faster, cheaper model
        temperature: 0.1,
        max_tokens: 200, // Minimal tokens
      });

      const content = response.choices[0]?.message?.content;
      if (!content) throw new Error('No AI detection response');

      const analysis = JSON.parse(this.cleanJsonResponse(content));

      return {
        isAIGenerated: analysis.aiGenerated || false,
        confidence: Math.min(100, Math.max(0, analysis.confidence || 50)),
        indicators: Array.isArray(analysis.indicators) ? analysis.indicators : [],
        humanScore: Math.min(100, Math.max(0, analysis.humanScore || 50)),
        reasoning: analysis.reasoning || 'Standard AI detection analysis'
      };
    } catch (error) {
      console.error('AI detection error:', error);
      // Fallback to pattern-based detection
      return this.fallbackDetection(userResponse);
    }
  }

  private performQuickChecks(response: string): { isObvious: boolean; indicators: string[] } {
    const indicators: string[] = [];
    const text = response.toLowerCase();

    // Common AI phrases
    const aiPhrases = [
      'as an ai', 'i am an ai', 'i cannot', 'i apologize, but',
      'however, it\'s important to note', 'it\'s worth noting that',
      'while i understand', 'from my training data',
      'based on my knowledge', 'in my opinion as an ai'
    ];

    // Overly structured responses
    const structurePatterns = [
      /^(first|firstly|1\.)/i,
      /\n(second|secondly|2\.)/i,
      /\n(third|thirdly|3\.)/i,
      /\n(finally|in conclusion)/i
    ];

    // Check for AI phrases
    for (const phrase of aiPhrases) {
      if (text.includes(phrase)) {
        indicators.push(`Contains AI phrase: "${phrase}"`);
      }
    }

    // Check for overly structured responses
    const structureMatches = structurePatterns.filter(pattern => pattern.test(response));
    if (structureMatches.length >= 3) {
      indicators.push('Overly structured numbered/bullet format');
    }

    // Check for excessive length relative to question complexity
    if (response.length > 800 && response.split('\n').length > 5) {
      indicators.push('Unusually detailed and structured response');
    }

    return {
      isObvious: indicators.length >= 2,
      indicators
    };
  }

  private fallbackDetection(response: string): AIDetectionResult {
    const quickCheck = this.performQuickChecks(response);
    const confidence = quickCheck.indicators.length * 25;

    return {
      isAIGenerated: confidence > 50,
      confidence: Math.min(100, confidence),
      indicators: quickCheck.indicators,
      humanScore: Math.max(0, 100 - confidence),
      reasoning: 'Pattern-based detection (AI analysis unavailable)'
    };
  }

  analyzeResponseWithAI(originalAnalysis: any, aiDetection: AIDetectionResult): ResponseAnalysis {
    let finalScore = originalAnalysis.overallScore || originalAnalysis.responseQuality || 0;
    let partialResultsOnly = false;

    // Apply AI penalty if detected
    if (aiDetection.isAIGenerated && aiDetection.confidence > 60) {
      // Significant penalty for likely AI usage
      finalScore = Math.max(0, finalScore * 0.3); // 70% penalty
      partialResultsOnly = true;
    } else if (aiDetection.confidence > 40) {
      // Moderate penalty for suspicious responses
      finalScore = Math.max(0, finalScore * 0.7); // 30% penalty
      partialResultsOnly = true;
    }

    return {
      originalAnalysis,
      aiDetection,
      finalScore: Math.round(finalScore),
      partialResultsOnly
    };
  }

  private cleanJsonResponse(content: string): string {
    // Remove markdown code blocks and extra whitespace
    return content
      .replace(/```json\n?/g, '')
      .replace(/```\n?/g, '')
      .replace(/^\s+|\s+$/g, '')
      .trim();
  }

  generateRecruiterFeedback(analysis: ResponseAnalysis): string {
    if (!analysis.aiDetection.isAIGenerated) {
      return "Response appears to be human-generated.";
    }

    const confidence = analysis.aiDetection.confidence;
    let feedback = `AI Usage Detected (${confidence}% confidence)\n`;

    if (confidence > 80) {
      feedback += "âš ï¸ High likelihood of AI assistance\n";
    } else if (confidence > 60) {
      feedback += "âš ï¸ Moderate likelihood of AI assistance\n";
    } else {
      feedback += "âš ï¸ Some indicators of possible AI assistance\n";
    }

    feedback += `Indicators: ${analysis.aiDetection.indicators.join(', ')}\n`;
    feedback += `Human-like score: ${analysis.aiDetection.humanScore}/100\n`;
    feedback += `Original score: ${analysis.originalAnalysis.overallScore || 'N/A'} â†’ Adjusted: ${analysis.finalScore}`;

    return feedback;
  }

  generateCandidateFeedback(analysis: ResponseAnalysis): string {
    if (!analysis.partialResultsOnly) {
      return ""; // No need to inform if no AI detected
    }

    return `Note: This assessment includes an AI authenticity check. Partial results shown. For complete evaluation, ensure responses reflect your personal knowledge and experience.`;
  }
}

export const aiDetectionService = new AIDetectionService();

server/apiKeyRotationService.ts.content:
import Groq from "groq-sdk";
import { Resend } from 'resend';

interface ApiKeyPool {
  keys: string[];
  currentIndex: number;
  failedKeys: Set<string>;
  lastFailureTime: Map<string, number>;
  cooldownPeriod: number; // in milliseconds
}

interface ServiceConfig {
  name: string;
  envPrefix: string;
  maxRetries: number;
  cooldownPeriod: number;
}

class ApiKeyRotationService {
  private groqPool: ApiKeyPool;
  private resendPool: ApiKeyPool;
  private groqClients: Map<string, Groq> = new Map();
  private resendClients: Map<string, Resend> = new Map();

  constructor() {
    // Initialize Groq key pool
    this.groqPool = this.initializeKeyPool('GROQ_API_KEY', {
      name: 'Groq',
      envPrefix: 'GROQ_API_KEY',
      maxRetries: 3,
      cooldownPeriod: 60000 // 1 minute cooldown
    });

    // Initialize Resend key pool
    this.resendPool = this.initializeKeyPool('RESEND_API_KEY', {
      name: 'Resend',
      envPrefix: 'RESEND_API_KEY',
      maxRetries: 3,
      cooldownPeriod: 300000 // 5 minute cooldown for email service
    });

    // Initialize clients
    this.initializeGroqClients();
    this.initializeResendClients();

    console.log(`ðŸ”„ API Key Rotation Service initialized:`);
    console.log(`   - Groq keys available: ${this.groqPool.keys.length}`);
    console.log(`   - Resend keys available: ${this.resendPool.keys.length}`);
  }

  private initializeKeyPool(envPrefix: string, config: ServiceConfig): ApiKeyPool {
    const keys: string[] = [];

    // Check for multiple API keys with numbered suffixes
    for (let i = 1; i <= 10; i++) {
      const keyName = i === 1 ? envPrefix : `${envPrefix}_${i}`;
      const key = process.env[keyName];
      if (key && key.trim()) {
        keys.push(key.trim());
      }
    }

    // If no numbered keys found, check the base key
    if (keys.length === 0) {
      const baseKey = process.env[envPrefix];
      if (baseKey && baseKey.trim()) {
        keys.push(baseKey.trim());
      }
    }

    return {
      keys,
      currentIndex: 0,
      failedKeys: new Set(),
      lastFailureTime: new Map(),
      cooldownPeriod: config.cooldownPeriod
    };
  }

  private initializeGroqClients(): void {
    this.groqPool.keys.forEach(key => {
      try {
        const client = new Groq({ apiKey: key });
        this.groqClients.set(key, client);
      } catch (error) {
        console.warn(`Failed to initialize Groq client for key: ${key.substring(0, 10)}...`);
      }
    });
  }

  private initializeResendClients(): void {
    this.resendPool.keys.forEach(key => {
      try {
        const client = new Resend(key);
        this.resendClients.set(key, client);
      } catch (error) {
        console.warn(`Failed to initialize Resend client for key: ${key.substring(0, 10)}...`);
      }
    });
  }

  private getNextWorkingKey(pool: ApiKeyPool): string | null {
    const now = Date.now();
    const totalKeys = pool.keys.length;

    if (totalKeys === 0) return null;

    // Try to find a working key, starting from current index
    for (let attempts = 0; attempts < totalKeys; attempts++) {
      const key = pool.keys[pool.currentIndex];

      // Check if key is in cooldown
      const lastFailure = pool.lastFailureTime.get(key);
      const isInCooldown = lastFailure && (now - lastFailure) < pool.cooldownPeriod;

      if (!pool.failedKeys.has(key) || !isInCooldown) {
        // Remove from failed keys if cooldown period has passed
        if (isInCooldown === false) {
          pool.failedKeys.delete(key);
          pool.lastFailureTime.delete(key);
        }

        return key;
      }

      // Move to next key
      pool.currentIndex = (pool.currentIndex + 1) % totalKeys;
    }

    // If all keys are failed and in cooldown, return the least recently failed
    let oldestFailureKey = pool.keys[0];
    let oldestFailureTime = pool.lastFailureTime.get(oldestFailureKey) || 0;

    for (const key of pool.keys) {
      const failureTime = pool.lastFailureTime.get(key) || 0;
      if (failureTime < oldestFailureTime) {
        oldestFailureTime = failureTime;
        oldestFailureKey = key;
      }
    }

    return oldestFailureKey;
  }

  private markKeyAsFailed(pool: ApiKeyPool, key: string): void {
    pool.failedKeys.add(key);
    pool.lastFailureTime.set(key, Date.now());

    // Move to next key
    const keyIndex = pool.keys.indexOf(key);
    if (keyIndex !== -1) {
      pool.currentIndex = (keyIndex + 1) % pool.keys.length;
    }

    console.warn(`ðŸš¨ API key marked as failed: ${key.substring(0, 10)}... (${pool.failedKeys.size}/${pool.keys.length} failed)`);
  }

  private isRateLimitError(error: any): boolean {
    const errorString = error?.toString()?.toLowerCase() || '';
    const errorMessage = error?.message?.toLowerCase() || '';
    const errorCode = error?.code?.toString() || '';
    const status = error?.status || error?.response?.status;

    return (
      status === 429 ||
      errorCode === '429' ||
      errorString.includes('rate limit') ||
      errorString.includes('too many requests') ||
      errorString.includes('quota exceeded') ||
      errorMessage.includes('rate limit') ||
      errorMessage.includes('too many requests') ||
      errorMessage.includes('quota exceeded')
    );
  }

  private isTemporaryError(error: any): boolean {
    const status = error?.status || error?.response?.status;
    return status >= 500 || this.isRateLimitError(error);
  }

  async executeWithGroqRotation<T>(
    operation: (client: Groq) => Promise<T>,
    maxRetries: number = 3
  ): Promise<T> {
    let lastError: any;

    for (let attempt = 0; attempt < maxRetries; attempt++) {
      const key = this.getNextWorkingKey(this.groqPool);

      if (!key) {
        throw new Error('No Groq API keys available');
      }

      const client = this.groqClients.get(key);
      if (!client) {
        this.markKeyAsFailed(this.groqPool, key);
        continue;
      }

      try {
        console.log(`ðŸ¤– Using Groq key: ${key.substring(0, 10)}... (attempt ${attempt + 1}/${maxRetries})`);
        const result = await operation(client);

        // Success - remove key from failed list if it was there
        this.groqPool.failedKeys.delete(key);
        this.groqPool.lastFailureTime.delete(key);

        return result;
      } catch (error) {
        lastError = error;
        console.error(`Groq operation failed with key ${key.substring(0, 10)}...:`, error);

        // Mark key as failed if it's a rate limit or server error
        if (this.isTemporaryError(error)) {
          this.markKeyAsFailed(this.groqPool, key);
        }

        // If it's not a temporary error, don't rotate keys
        if (!this.isTemporaryError(error)) {
          throw error;
        }

        // Short delay before retry
        if (attempt < maxRetries - 1) {
          await new Promise(resolve => setTimeout(resolve, 1000 * (attempt + 1)));
        }
      }
    }

    throw lastError || new Error('All Groq API keys exhausted');
  }

  async executeWithResendRotation<T>(
    operation: (client: Resend) => Promise<T>,
    maxRetries: number = 2
  ): Promise<T> {
    let lastError: any;

    for (let attempt = 0; attempt < maxRetries; attempt++) {
      const key = this.getNextWorkingKey(this.resendPool);

      if (!key) {
        throw new Error('No Resend API keys available');
      }

      const client = this.resendClients.get(key);
      if (!client) {
        this.markKeyAsFailed(this.resendPool, key);
        continue;
      }

      try {
        console.log(`ðŸ“§ Using Resend key: ${key.substring(0, 10)}... (attempt ${attempt + 1}/${maxRetries})`);
        const result = await operation(client);

        // Success - remove key from failed list if it was there
        this.resendPool.failedKeys.delete(key);
        this.resendPool.lastFailureTime.delete(key);

        return result;
      } catch (error) {
        lastError = error;
        console.error(`Resend operation failed with key ${key.substring(0, 10)}...:`, error);

        // Mark key as failed if it's a rate limit or server error
        if (this.isTemporaryError(error)) {
          this.markKeyAsFailed(this.resendPool, key);
        }

        // If it's not a temporary error, don't rotate keys
        if (!this.isTemporaryError(error)) {
          throw error;
        }

        // Longer delay for email service
        if (attempt < maxRetries - 1) {
          await new Promise(resolve => setTimeout(resolve, 2000 * (attempt + 1)));
        }
      }
    }

    throw lastError || new Error('All Resend API keys exhausted');
  }

  getStatus() {
    return {
      groq: {
        totalKeys: this.groqPool.keys.length,
        failedKeys: this.groqPool.failedKeys.size,
        currentIndex: this.groqPool.currentIndex,
        availableKeys: this.groqPool.keys.length - this.groqPool.failedKeys.size
      },
      resend: {
        totalKeys: this.resendPool.keys.length,
        failedKeys: this.resendPool.failedKeys.size,
        currentIndex: this.resendPool.currentIndex,
        availableKeys: this.resendPool.keys.length - this.resendPool.failedKeys.size
      }
    };
  }

  // Method to manually reset failed keys (useful for admin endpoints)
  resetFailedKeys(service?: 'groq' | 'resend'): void {
    if (!service || service === 'groq') {
      this.groqPool.failedKeys.clear();
      this.groqPool.lastFailureTime.clear();
      console.log('ðŸ”„ Groq failed keys reset');
    }

    if (!service || service === 'resend') {
      this.resendPool.failedKeys.clear();
      this.resendPool.lastFailureTime.clear();
      console.log('ðŸ”„ Resend failed keys reset');
    }
  }
}

// Export singleton instance
export const apiKeyRotationService = new ApiKeyRotationService();

