// Enhanced Custom NLP Service for Job Analysis
// Improved accuracy, comprehensive analysis, and better maintainability

interface JobAnalysisResult {
  matchScore: number;
  confidenceLevel: 'high' | 'medium' | 'low';
  matchingSkills: SkillMatch[];
  missingSkills: SkillGap[];
  skillGaps: {
    critical: SkillGap[];
    important: SkillGap[];
    nice_to_have: SkillGap[];
  };
  seniorityLevel: string;
  workMode: string;
  jobType: string;
  roleComplexity: string;
  careerProgression: string;
  industryFit: string;
  cultureFit: string;
  applicationRecommendation: ApplicationRecommendation;
  tailoringAdvice: string[];
  interviewPrepTips: string[];
  riskFactors: string[];
  growthOpportunities: string[];
  salary?: SalaryInfo;
  extractedData: ExtractedJobData;
  analysisMetadata: AnalysisMetadata;
}

interface SkillMatch {
  skill: string;
  userSkill?: string;
  matchType: 'exact' | 'partial' | 'synonym' | 'related';
  relevance: 'high' | 'medium' | 'low';
  yearsExperience?: number;
}

interface SkillGap {
  skill: string;
  category: 'technical' | 'soft' | 'domain' | 'certification';
  priority: 'critical' | 'important' | 'nice_to_have';
  learningTime: string;
  alternatives?: string[];
}

interface ApplicationRecommendation {
  action: 'strongly_recommended' | 'recommended' | 'consider_with_preparation' | 'needs_development' | 'not_suitable';
  reasoning: string[];
  timeline?: string;
  preparationSteps?: string[];
}

interface SalaryInfo {
  min?: number;
  max?: number;
  currency: string;
  isEstimated: boolean;
  source: 'description' | 'inferred';
  marketRate?: {
    percentile: number;
    comparison: 'above' | 'at' | 'below';
  };
}

interface ExtractedJobData {
  title: string;
  normalizedTitle: string;
  company: string;
  location: string;
  isRemote: boolean;
  requiredSkills: ParsedSkill[];
  preferredSkills: ParsedSkill[];
  qualifications: Qualification[];
  benefits: string[];
  responsibilities: string[];
  teamSize?: number;
  reportingStructure?: string;
  industry?: string;
  companyStage?: string;
}

interface ParsedSkill {
  name: string;
  category: 'technical' | 'soft' | 'domain' | 'certification';
  isRequired: boolean;
  yearsRequired?: number;
  context?: string;
  alternatives?: string[];
}

interface Qualification {
  type: 'education' | 'experience' | 'certification' | 'other';
  requirement: string;
  isRequired: boolean;
  alternatives?: string[];
}

interface AnalysisMetadata {
  processingTime: number;
  textLength: number;
  extractionConfidence: number;
  version: string;
  timestamp: Date;
}

