  // Question bank operations
  async getQuestionBankQuestions(filters?: { type?: string; difficulty?: string; limit?: number }): Promise<any[]> {
    return await handleDbOperation(async () => {
      let query = db.select().from(questionBank).where(eq(questionBank.isActive, true));

      // Apply filters if provided
      if (filters?.type) {
        query = query.where(eq(questionBank.type, filters.type));
      }
      if (filters?.difficulty) {
        query = query.where(eq(questionBank.difficulty, filters.difficulty));
      }

      let results = await query.orderBy(sql`RANDOM()`);

      if (filters?.limit) {
        results = results.slice(0, filters.limit);
      }

      return results;
    }, []);
  }

  // User interview stats
  async getUserInterviewStats(userId: string): Promise<UserInterviewStats | undefined> {
    return await handleDbOperation(async () => {
      let [stats] = await db.select().from(userInterviewStats).where(eq(userInterviewStats.userId, userId));

      // If no stats exist, create default stats
      if (!stats) {
        const defaultStats = {
          userId,
          totalInterviews: 0,
          completedInterviews: 0,
          averageScore: 0,
          freeInterviewsUsed: 0,
          bestScore: 0,
          totalTimeSpent: 0
        };

        [stats] = await db.insert(userInterviewStats).values(defaultStats).returning();
      }

      return stats;
    }, undefined);
  }

  async upsertUserInterviewStats(stats: InsertUserInterviewStats): Promise<UserInterviewStats> {
    return await handleDbOperation(async () => {
      const [upsertedStats] = await db
        .insert(userInterviewStats)
        .values(stats)
        .onConflictDoUpdate({
          target: userInterviewStats.userId,
          set: {
            ...stats,
            updatedAt: new Date(),
          },
        })
        .returning();
      return upsertedStats;
    });
  }
}

