/* Import custom animations */
@import './styles/animations.css';

/* Performance & SEO Optimizations */
@layer base {
  /* Improve font loading performance */
  html {
    font-display: swap;
    font-feature-settings: "liga" 1, "calt" 1, "kern" 1;
    scroll-behavior: smooth;
    text-rendering: optimizeLegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  /* Performance optimizations */
  * {
    box-sizing: border-box;
  }

  /* Critical CSS for above-the-fold content */
  body {
    margin: 0;
    padding: 0;
    min-height: 100vh;
    line-height: 1.5;
    -webkit-text-size-adjust: 100%;
    background-color: hsl(var(--background));
    color: hsl(var(--foreground));
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
    overflow-x: hidden; /* Prevent horizontal scroll */
    width: 100%;
  }

  /* Ensure all containers don't exceed viewport width */
  #root {
    width: 100%;
    min-height: 100vh;
    overflow-x: hidden;
  }

  /* Mobile-specific fixes */
  @media (max-width: 768px) {
    html {
      font-size: 16px; /* Prevent zoom on iOS */
    }

    body {
      -webkit-touch-callout: none;
      -webkit-user-select: none;
      -webkit-tap-highlight-color: transparent;
    }

    /* Ensure no horizontal scroll */
    * {
      max-width: 100vw;
    }

    /* Fix for mobile viewport height */
    .min-h-screen {
      min-height: 100vh;
      min-height: 100dvh; /* Dynamic viewport height */
    }
  }

  /* Accessibility improvements */
  :focus-visible {
    outline: 2px solid hsl(var(--ring));
    outline-offset: 2px;
  }

  /* Smooth transitions for better UX */
  a, button {
    transition: all 0.2s ease-in-out;
  }

  /* Auto-sliding animation for recruiter features */
  @keyframes slideLoop {
    0% {
      transform: translateX(0);
    }
    100% {
      transform: translateX(-66.666%); /* Move by 2/3 to show next 2 slides */
    }
  }

  .animate-slideLoop {
    animation: slideLoop 15s linear infinite;
  }

  /* Pause animation on hover */
  .animate-slideLoop:hover {
    animation-play-state: paused;
  }

  /* Image optimization */
  img {
    max-width: 100%;
    height: auto;
    display: block;
  }

  /* Prevent layout shift */
  .loading-skeleton {
    background: linear-gradient(90deg, 
      hsl(var(--muted)) 25%, 
      transparent 37%, 
      hsl(var(--muted)) 63%);
    background-size: 400% 100%;
    animation: skeleton-loading 1.4s ease infinite;
  }

  @keyframes skeleton-loading {
    0% {
      background-position: 100% 50%;
    }
    100% {
      background-position: -100% 50%;
    }
  }
}

/* Component optimizations */
@layer components {
  /* High-performance button styles */
  .btn-primary {
    @apply bg-primary text-primary-foreground hover:bg-primary/90 
           transition-all duration-200 ease-in-out
           focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2;
    transform: translateZ(0); /* Hardware acceleration */
  }

  /* Optimized card component */
