client/src/components/PaymentGatewaySele.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { CreditCard, Wallet, DollarSign, CheckCircle, AlertCircle } from "lucide-react";
import PayPalSubscriptionButton from "./PayPalSubscriptionButton";

interface PaymentGatewayProps {
  tierId: string;
  tierName: string;
  amount: number;
  currency: string;
  userType: 'jobseeker' | 'recruiter';
  onPaymentSuccess?: (data: any) => void;
  onPaymentError?: (error: any) => void;
  description?: string;
  className?: string;
}

export default function PaymentGatewaySelector({
  tierId,
  tierName,
  amount,
  currency,
  userType,
  onPaymentSuccess,
  onPaymentError,
  description,
  className
}: PaymentGatewayProps) {
  const [selectedGateway, setSelectedGateway] = useState<'paypal' | 'cashfree' | 'razorpay'>('paypal');
  const [isProcessing, setIsProcessing] = useState(false);

  const paymentGateways = [
    {
      id: 'paypal',
      name: 'PayPal',
      icon: <div className="w-6 h-6 bg-blue-600 text-white rounded flex items-center justify-center text-xs font-bold">P</div>,
      description: 'Pay securely with PayPal',
      status: 'active',
      badge: 'Primary',
      badgeColor: 'bg-green-100 text-green-800'
    },
    {
      id: 'cashfree',
      name: 'Cashfree',
      icon: <Wallet className="w-6 h-6 text-orange-600" />,
      description: 'UPI, Cards, Netbanking & Wallets',
      status: 'setup',
      badge: 'Coming Soon',
      badgeColor: 'bg-orange-100 text-orange-800'
    },
    {
      id: 'razorpay',
      name: 'Razorpay',
      icon: <CreditCard className="w-6 h-6 text-blue-600" />,
      description: 'Cards, UPI, BNPL & more payment options',
      status: 'setup',
      badge: 'Coming Soon',
      badgeColor: 'bg-blue-100 text-blue-800'
    }
  ];

  const handlePayPalSuccess = (data: any) => {
    setIsProcessing(false);
    onPaymentSuccess?.(data);
  };

  const handlePayPalError = (error: any) => {
    setIsProcessing(false);
    onPaymentError?.(error);
  };

  const handleComingSoonPayment = (gatewayName: string) => {
    onPaymentError?.({
      message: `${gatewayName} integration is coming soon. Please use PayPal for now.`
    });
  };

  return (
    <div className={`space-y-6 ${className}`}>
      {/* Payment Gateway Selection */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <DollarSign className="w-5 h-5" />
            Select Payment Method
          </CardTitle>
          {description && <CardDescription>{description}</CardDescription>}
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Gateway Options */}
          <div className="grid gap-3">
            {paymentGateways.map((gateway) => (
              <div
                key={gateway.id}
                className={`p-4 border rounded-lg cursor-pointer transition-all ${
                  selectedGateway === gateway.id
                    ? 'border-blue-500 bg-blue-50 dark:bg-blue-950/20'
                    : gateway.status === 'active'
                    ? 'border-gray-200 hover:border-gray-300'
                    : 'border-gray-100 bg-gray-50 dark:bg-gray-900'
                }`}
                onClick={() => {
                  if (gateway.status === 'active') {
                    setSelectedGateway(gateway.id as any);
                  }
                }}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    {gateway.icon}
                    <div>
                      <div className="font-medium flex items-center gap-2">
                        {gateway.name}
                        <Badge className={`text-xs ${gateway.badgeColor}`}>
                          {gateway.badge}
                        </Badge>
                      </div>
                      <div className="text-sm text-muted-foreground">
                        {gateway.description}
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    {gateway.status === 'active' ? (
                      <CheckCircle className="w-5 h-5 text-green-500" />
                    ) : (
                      <AlertCircle className="w-5 h-5 text-orange-500" />
                    )}
                    <div className={`w-4 h-4 rounded-full border-2 ${
                      selectedGateway === gateway.id && gateway.status === 'active'
                        ? 'border-blue-500 bg-blue-500'
                        : 'border-gray-300'
                    }`}>
                      {selectedGateway === gateway.id && gateway.status === 'active' && (
                        <div className="w-full h-full rounded-full bg-white scale-50"></div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Payment Amount Display */}
          <div className="bg-gray-50 dark:bg-gray-900 rounded-lg p-4">
            <div className="flex justify-between items-center">
              <span className="font-medium">Total Amount:</span>
              <span className="text-lg font-bold">
                {currency.toUpperCase()} {amount}
              </span>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Payment Button */}
      <Card>
        <CardContent className="pt-6">
          {selectedGateway === 'paypal' ? (
            <div className="space-y-4">
              <div className="text-center">
                <h3 className="font-semibold">Complete Payment with PayPal</h3>
                <p className="text-sm text-muted-foreground">
                  Click the button below to proceed with secure PayPal payment
                </p>
              </div>
              <div className="flex justify-center">
                <PayPalSubscriptionButton
                  tierId={tierId}
                  tierName={tierName}
                  price={amount}
                  userType={userType}
                  onSuccess={(subscriptionId: string) => {
                    onPaymentSuccess?.({ subscriptionId });
                  }}
                />
              </div>
            </div>
          ) : selectedGateway === 'cashfree' ? (
            <div className="text-center space-y-4">
              <div className="text-orange-600">
                <Wallet className="w-12 h-12 mx-auto mb-2" />
                <h3 className="font-semibold">Cashfree Integration Coming Soon</h3>
                <p className="text-sm text-muted-foreground">
                  We're working on integrating Cashfree for UPI and card payments.
                </p>
              </div>
              <Button
                variant="outline"
                onClick={() => handleComingSoonPayment('Cashfree')}
                className="w-full"
              >
                Notify Me When Available
              </Button>
            </div>
          ) : selectedGateway === 'razorpay' ? (
            <div className="text-center space-y-4">
              <div className="text-blue-600">
                <CreditCard className="w-12 h-12 mx-auto mb-2" />
                <h3 className="font-semibold">Razorpay Integration Coming Soon</h3>
                <p className="text-sm text-muted-foreground">
                  We're working on integrating Razorpay for comprehensive payment options.
                </p>
              </div>
              <Button
                variant="outline"
                onClick={() => handleComingSoonPayment('Razorpay')}
                className="w-full"
              >
                Notify Me When Available
              </Button>
            </div>
          ) : null}
        </CardContent>
      </Card>

      {/* Payment Security Info */}
      <div className="bg-green-50 dark:bg-green-950/20 border border-green-200 dark:border-green-800 rounded-lg p-4">
        <div className="flex items-start gap-3">
          <CheckCircle className="w-5 h-5 text-green-600 flex-shrink-0 mt-0.5" />
          <div className="text-sm">
            <div className="font-medium text-green-800 dark:text-green-200">
              Secure Payment Processing
            </div>
            <div className="text-green-700 dark:text-green-300">
              All payments are processed securely with industry-standard encryption. 
              Your payment information is never stored on our servers.
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/PremiumGate.tsx.content:
import { ReactNode } from "react";
import { useUsageEnforcement } from "@/hooks/useUsageEnforcement";
import FeatureBlockModal from "@/components/FeatureBlockModal";
import { useAuth } from "@/hooks/use-auth";

interface PremiumGateProps {
  children: ReactNode;
  feature: string;
  blockOnLimit?: boolean;
}

/**
 * PremiumGate component that wraps features and enforces premium upgrades
 * blockOnLimit: if true, completely blocks access when limit reached
 */
export default function PremiumGate({ children, feature, blockOnLimit = true }: PremiumGateProps) {
  const { user } = useAuth();
  const { 
    isFeatureBlocked, 
    showUpgradeModal, 
    setShowUpgradeModal, 
    blockedFeature,
    getRemainingUsage
  } = useUsageEnforcement();

  const isBlocked = blockOnLimit && isFeatureBlocked(feature);
  const remaining = getRemainingUsage(feature);

  if (isBlocked) {
    return (
      <>
        <div className="relative">
          <div className="absolute inset-0 bg-gray-900/50 backdrop-blur-sm z-10 flex items-center justify-center">
            <div className="text-center p-6 bg-white dark:bg-gray-800 rounded-lg shadow-lg max-w-md">
              <div className="w-16 h-16 bg-gradient-to-br from-yellow-400 to-orange-500 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-2xl">ðŸ”’</span>
              </div>
              <h3 className="text-xl font-bold mb-2">Premium Required</h3>
              <p className="text-gray-600 dark:text-gray-400 mb-4">
                You've used all your free {feature} allowances this month.
              </p>
              <button 
                onClick={() => setShowUpgradeModal(true)}
                className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 py-2 rounded-lg font-semibold hover:from-blue-700 hover:to-purple-700 transition-all"
              >
                Upgrade to Premium
              </button>
            </div>
          </div>
          <div className="opacity-30 pointer-events-none">
            {children}
          </div>
        </div>

        <FeatureBlockModal
          isOpen={showUpgradeModal}
          onClose={() => setShowUpgradeModal(false)}
          feature={blockedFeature || feature}
          userType={user?.userType}
        />
      </>
    );
  }

  // Show warning when approaching limits
  if (remaining <= 1 && remaining > 0) {
    return (
      <>
        <div className="relative">
          <div className="border-2 border-yellow-400 rounded-lg">
            <div className="bg-yellow-50 dark:bg-yellow-950 p-2 rounded-t-lg border-b border-yellow-200">
              <p className="text-sm text-yellow-800 dark:text-yellow-200 text-center">
                âš ï¸ Last {feature} remaining! Upgrade to premium for unlimited access.
              </p>
            </div>
            {children}
          </div>
        </div>

        <FeatureBlockModal
          isOpen={showUpgradeModal}
          onClose={() => setShowUpgradeModal(false)}
          feature={feature}
          userType={user?.userType}
        />
      </>
    );
  }

  return (
    <>
      {children}
      <FeatureBlockModal
        isOpen={showUpgradeModal}
        onClose={() => setShowUpgradeModal(false)}
        feature={feature}
        userType={user?.userType}
      />
    </>
  );
}

client/src/components/PremiumPromptModal.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Crown, Zap, AlertTriangle, TrendingUp } from "lucide-react";
import { useLocation } from "wouter";

interface PremiumPromptModalProps {
  isOpen: boolean;
  onClose: () => void;
  feature?: string;
  usagePercentage?: number;
}

export default function PremiumPromptModal({ isOpen, onClose, feature, usagePercentage }: PremiumPromptModalProps) {
  const [, setLocation] = useLocation();

  const { data: usageReport } = useQuery({
    queryKey: ['/api/usage/report'],
    enabled: isOpen,
  });

  const handleUpgrade = () => {
    // Determine if user is recruiter or job seeker based on current route
    const isRecruiter = window.location.pathname.includes('recruiter') || 
                       window.location.pathname.includes('post-job') ||
                       window.location.pathname.includes('dashboard');

    if (isRecruiter) {
      setLocation('/recruiter-premium');
    } else {
      setLocation('/job-seeker-premium');
    }
    onClose();
  };

  const getFeatureMessage = (feature?: string) => {
    switch (feature) {
      case 'jobAnalyses':
        return 'You\'ve reached your job analysis limit for this month.';
      case 'resumeAnalyses':
        return 'You\'ve reached your resume analysis limit for this month.';
      case 'applications':
        return 'You\'ve reached your application limit for this month.';
      case 'autoFills':
        return 'You\'ve reached your auto-fill limit for this month.';
      case 'jobPostings':
        return 'You\'ve reached your job posting limit for this month.';
      case 'interviews':
        return 'You\'ve reached your interview limit for this month.';
      case 'candidates':
        return 'You\'ve reached your candidate limit for this month.';
      default:
        return 'You\'ve reached your usage limits for this month.';
    }
  };

  const isFreeTier = !usageReport?.subscription || !usageReport?.subscription.isActive;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            {isFreeTier ? (
              <>
                <Crown className="h-5 w-5 text-yellow-600" />
                Upgrade to Premium
              </>
            ) : (
              <>
                <AlertTriangle className="h-5 w-5 text-yellow-600" />
                Usage Limit Reached
              </>
            )}
          </DialogTitle>
          <DialogDescription>
            {feature ? getFeatureMessage(feature) : 'Upgrade now to continue using all features.'}
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {usagePercentage && (
            <Card className="border-yellow-200 bg-yellow-50 dark:bg-yellow-950">
              <CardContent className="pt-4">
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm font-medium">Current Usage</span>
                  <span className="text-sm text-yellow-700 dark:text-yellow-300">
                    {usagePercentage}% used
                  </span>
                </div>
                <Progress value={usagePercentage} className="h-2" />
              </CardContent>
            </Card>
          )}

          <div className="space-y-3">
            <h4 className="font-semibold text-sm">Premium Benefits:</h4>
            <div className="space-y-2">
              <div className="flex items-center gap-2">
                <TrendingUp className="h-4 w-4 text-green-600" />
                <span className="text-sm">Unlimited usage of all features</span>
              </div>
              <div className="flex items-center gap-2">
                <Zap className="h-4 w-4 text-blue-600" />
                <span className="text-sm">AI-powered advanced features</span>
              </div>
              <div className="flex items-center gap-2">
                <Crown className="h-4 w-4 text-purple-600" />
                <span className="text-sm">Priority support and analytics</span>
              </div>
            </div>
          </div>

          <div className="flex gap-3 pt-4">
            <Button variant="outline" onClick={onClose} className="flex-1">
              Maybe Later
            </Button>
            <Button onClick={handleUpgrade} className="flex-1">
              <Crown className="h-4 w-4 mr-2" />
              Upgrade Now
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/RecruiterNavbar.ts.content:
import { useState } from "react";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { 
  Building,
  Users,
  Briefcase,
  FileText,
  Target,
  BarChart3,
  Settings,
  Crown,
  Menu,
  X,
  Zap,
  Star,
  Bell,
  GitBranch,
  Video,
  LogOut,
  User,
  ChevronDown,
  Home,
  TrendingUp,
  MessageCircle
} from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface User {
  id: string;
  email: string;
  firstName: string;
  lastName: string;
  companyName: string;
  planType: string;
  subscriptionStatus: string;
}

interface RecruiterNavbarProps {
  user?: User;
}

export function RecruiterNavbar({ user }: RecruiterNavbarProps) {
  const [location, setLocation] = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Get unread message count for notifications (load once per session)
  const { data: conversations = [] } = useQuery<any[]>({
    queryKey: ['/api/chat/conversations'],
    enabled: !!user?.id,
    staleTime: 5 * 60 * 1000, // Consider data fresh for 5 minutes
    // No automatic refresh - updates when user navigates or manually refreshes
  });

  const unreadCount = conversations.reduce((total: number, conv: any) => {
    return total + (conv.unreadCount || 0);
  }, 0);

  // Logout mutation
  const logoutMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/auth/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include', // Important for session-based auth
      });

      if (!response.ok) {
        throw new Error('Logout failed');
      }

      return await response.json();
    },
    onSuccess: () => {
      // Clear all cached data
      queryClient.clear();
      // Show success message
      toast({
        title: "Logged out successfully",
        description: "You have been logged out of your account.",
      });
      // Redirect to auth page
      setLocation('/auth');
    },
    onError: (error: any) => {
      toast({
        title: "Logout failed",
        description: error.message || "Failed to logout properly",
        variant: "destructive",
      });
    }
  });

  const getPlanBadge = (planType: string) => {
    switch (planType) {
      case 'premium':
        return <Badge className="bg-gradient-to-r from-purple-500 to-pink-500 text-white"><Crown className="w-3 h-3 mr-1" />Premium</Badge>;
      case 'enterprise':
        return <Badge className="bg-gradient-to-r from-amber-500 to-orange-500 text-white"><Star className="w-3 h-3 mr-1" />Enterprise</Badge>;
      default:
        return <Badge variant="outline">Free</Badge>;
    }
  };

  const navigationItems = [
    {
      name: "Dashboard",
      href: "/recruiter/dashboard",
      icon: BarChart3,
      current: location === "/recruiter/dashboard"
    },
    {
      name: "Job Postings",
      href: "/recruiter/jobs",
      icon: Briefcase,
      current: location === "/recruiter/jobs"
    },

    {
      name: "Pipeline",
      href: "/recruiter/pipeline",
      icon: GitBranch,
      current: location === "/recruiter/pipeline"
    },
    {
      name: "Messages",
      href: "/chat",
      icon: MessageCircle,
      current: location === "/chat",
      badge: unreadCount > 0 ? unreadCount : undefined
    },
    {
      name: "Interview Assignments",
      href: "/recruiter/interview-assignments",
      icon: Video,
      current: location === "/recruiter/interview-assignments"
    },
    {
      name: "Test Center",
      href: "/recruiter/tests",
      icon: FileText,
      current: location === "/recruiter/tests"
    },
    {
      name: "Premium Targeting",
      href: "/premium-targeting",
      icon: Target,
      current: location === "/premium-targeting",
      premium: true
    },
    {
      name: "Analytics",
      href: "/recruiter/analytics",
      icon: BarChart3,
      current: location === "/recruiter/analytics",
      premium: true
    }
  ];

  const canAccessFeature = (isPremium: boolean) => {
    if (!isPremium) return true;
    return user?.planType === 'premium' || user?.planType === 'enterprise';
  };

  return (
    <>
      <nav className="bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex">
              {/* Logo */}
              <div className="flex-shrink-0 flex items-center">
                <Link href="/recruiter/dashboard">
                  <div className="flex items-center space-x-2 cursor-pointer">
                    <Building className="h-8 w-8 text-blue-600" />
                    <span className="font-bold text-xl text-gray-900 dark:text-white">AutoJobr</span>
                  </div>
                </Link>
              </div>

              {/* Desktop Navigation */}
              <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
                {navigationItems.map((item) => {
                  const Icon = item.icon;
                  const canAccess = canAccessFeature(item.premium || false);

                  return (
                    <Link
                      key={item.name}
                      href={canAccess ? item.href : "/recruiter/premium"}
                      className={`${
                        item.current
                          ? "border-blue-500 text-gray-900 dark:text-white"
                          : "border-transparent text-gray-500 dark:text-gray-300 hover:border-gray-300 hover:text-gray-700 dark:hover:text-gray-200"
                      } inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium transition-colors ${
                        !canAccess ? "opacity-50" : ""
                      }`}
                    >
                      <Icon className="w-4 h-4 mr-2" />
                      {item.name}
                      {item.badge && (
                        <Badge className="ml-2 bg-red-500 text-white text-xs px-1 py-0 min-w-[1rem] h-5">
                          {item.badge > 9 ? '9+' : item.badge}
                        </Badge>
                      )}
                      {item.premium && !canAccess && (
                        <Crown className="w-3 h-3 ml-1 text-amber-500" />
                      )}
                    </Link>
                  );
                })}
              </div>
            </div>

            {/* Right side */}
            <div className="hidden md:ml-6 md:flex md:items-center md:space-x-4">
              {/* Notifications */}
              <Link href="/messaging">
                <button className="relative p-1 text-gray-400 hover:text-gray-500 focus:outline-none">
                  <Bell className="h-6 w-6" />
                  {unreadCount > 0 && (
                    <span className="absolute -top-1 -right-1 flex h-5 w-5 items-center justify-center rounded-full bg-red-500 text-xs font-medium text-white ring-2 ring-white">
                      {unreadCount > 9 ? '9+' : unreadCount}
                    </span>
                  )}
                </button>
              </Link>

              {/* Upgrade Button for Free Users */}
              {user?.planType === 'free' && (
                <Link href="/recruiter/premium">
                  <Button size="sm" className="bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white">
                    <Zap className="w-4 h-4 mr-2" />
                    Upgrade
                  </Button>
                </Link>
              )}

              {/* User Profile Dropdown */}
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="ghost" className="relative h-10 w-auto px-3 hover:bg-gray-100 dark:hover:bg-gray-700">
                    <div className="flex items-center space-x-3">
                      <Avatar className="h-8 w-8">
                        <AvatarImage src={`https://api.dicebear.com/6.x/initials/svg?seed=${user?.firstName} ${user?.lastName}`} />
                        <AvatarFallback className="bg-blue-600 text-white">
                          {user?.firstName?.[0]}{user?.lastName?.[0]}
