                      </div>
                      <ChevronDown className="h-4 w-4 text-gray-400" />
                    </div>
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent className="w-56" align="end" forceMount>
                  <DropdownMenuLabel className="font-normal">
                    <div className="flex flex-col space-y-1">
                      <p className="text-sm font-medium leading-none">
                        {user?.firstName} {user?.lastName}
                      </p>
                      <p className="text-xs leading-none text-muted-foreground">
                        {user?.email}
                      </p>
                      <div className="pt-1">
                        {getPlanBadge(user?.planType || 'free')}
                      </div>
                    </div>
                  </DropdownMenuLabel>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem asChild>
                    <Link href="/recruiter/profile" className="w-full flex items-center">
                      <User className="mr-2 h-4 w-4" />
                      <span>Profile Settings</span>
                    </Link>
                  </DropdownMenuItem>
                  <DropdownMenuItem asChild>
                    <Link href="/recruiter/billing" className="w-full flex items-center">
                      <Crown className="mr-2 h-4 w-4" />
                      <span>Billing & Plans</span>
                    </Link>
                  </DropdownMenuItem>
                  <DropdownMenuItem asChild>
                    <Link href="/recruiter/settings" className="w-full flex items-center">
                      <Settings className="mr-2 h-4 w-4" />
                      <span>Account Settings</span>
                    </Link>
                  </DropdownMenuItem>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem 
                    onClick={() => logoutMutation.mutate()}
                    disabled={logoutMutation.isPending}
                    className="text-red-600 focus:text-red-600 cursor-pointer"
                  >
                    <LogOut className="mr-2 h-4 w-4" />
                    <span>{logoutMutation.isPending ? 'Logging out...' : 'Logout'}</span>
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </div>

            {/* Mobile menu button */}
            <div className="sm:hidden flex items-center">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              >
                {mobileMenuOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
              </Button>
            </div>
          </div>
        </div>

        {/* Mobile menu */}
        {mobileMenuOpen && (
          <div className="sm:hidden border-t border-gray-200 dark:border-gray-700">
            <div className="pt-2 pb-3 space-y-1">
              {navigationItems.map((item) => {
                const Icon = item.icon;
                const canAccess = canAccessFeature(item.premium || false);

                return (
                  <Link
                    key={item.name}
                    href={canAccess ? item.href : "/recruiter/premium"}
                    className={`${
                      item.current
                        ? "bg-blue-50 dark:bg-blue-900 border-blue-500 text-blue-700 dark:text-blue-200"
                        : "border-transparent text-gray-500 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 hover:border-gray-300 hover:text-gray-700 dark:hover:text-gray-200"
                    } block pl-3 pr-4 py-2 border-l-4 text-base font-medium transition-colors ${
                      !canAccess ? "opacity-50" : ""
                    }`}
                    onClick={() => setMobileMenuOpen(false)}
                  >
                    <div className="flex items-center">
                      <Icon className="w-4 h-4 mr-3" />
                      {item.name}
                      {item.premium && !canAccess && (
                        <Crown className="w-3 h-3 ml-2 text-amber-500" />
                      )}
                    </div>
                  </Link>
                );
              })}

              {/* Mobile Plan Info */}
              <div className="px-3 py-2 border-t border-gray-200 dark:border-gray-700">
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-500 dark:text-gray-400">Current Plan:</span>
                  {user && getPlanBadge(user.planType)}
                </div>
                {user?.planType === 'free' && (
                  <Link href="/recruiter/premium">
                    <Button className="w-full mt-2 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white">
                      <Zap className="w-4 h-4 mr-2" />
                      Upgrade to Premium
                    </Button>
                  </Link>
                )}
              </div>
            </div>
          </div>
        )}
      </nav>
    </>
  );
}

client/src/components/UsageMonitoringWid.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { AlertTriangle, Crown, TrendingUp, Zap } from "lucide-react";
import { useLocation } from "wouter";

interface UsageReport {
  subscription: any;
  usage: Record<string, number>;
  limits: Record<string, number>;
  percentages: Record<string, number>;
  upgradeRecommended: boolean;
}

export default function UsageMonitoringWidget() {
  const [, setLocation] = useLocation();

  const { data: usageReport, isLoading, error } = useQuery({
    queryKey: ['/api/usage/report'],
    refetchInterval: 60000, // Refresh every minute
    retry: 1, // Only retry once to avoid excessive requests
  });

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center justify-center">
            <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-primary"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (error || !usageReport) {
    return (
      <Card>
        <CardContent className="p-4 text-center text-muted-foreground">
          <p>Unable to load usage data</p>
        </CardContent>
      </Card>
    );
  }

  const report: UsageReport = usageReport;
  const isFreeTier = !report.subscription || !report.subscription.isActive;
  const showUpgradePrompt = report.upgradeRecommended || isFreeTier;

  const getUsageColor = (percentage: number) => {
    if (percentage >= 90) return "text-red-600";
    if (percentage >= 70) return "text-yellow-600";
    return "text-green-600";
  };

  const getProgressColor = (percentage: number) => {
    if (percentage >= 90) return "bg-red-500";
    if (percentage >= 70) return "bg-yellow-500";
    return "bg-green-500";
  };

  const formatLimit = (limit: number) => {
    if (limit === -1) return "Unlimited";
    return limit.toLocaleString();
  };

  const formatUsageItem = (key: string) => {
    const labels: Record<string, string> = {
      jobAnalyses: "Job Analyses",
      resumeAnalyses: "Resume Analyses", 
      applications: "Applications",
      autoFills: "Auto-fills",
      jobPostings: "Job Postings",
      interviews: "Interviews",
      candidates: "Candidates"
    };
    return labels[key] || key;
  };

  return (
    <div className="space-y-4">
      {showUpgradePrompt && (
        <Card className="border-yellow-200 bg-yellow-50 dark:bg-yellow-950 dark:border-yellow-800">
          <CardHeader className="pb-3">
            <CardTitle className="flex items-center gap-2 text-yellow-800 dark:text-yellow-200">
              <Crown className="h-5 w-5" />
              {isFreeTier ? "Upgrade to Premium" : "Usage Limit Warning"}
            </CardTitle>
            <CardDescription className="text-yellow-700 dark:text-yellow-300">
              {isFreeTier 
                ? "You're on the free tier with limited features. Upgrade now for unlimited access!"
                : "You're approaching your monthly limits. Consider upgrading for more usage."
              }
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Button 
              onClick={() => {
                // Get user type from actual API data rather than URL guessing
                if (report.subscription?.userType === 'recruiter') {
                  setLocation('/recruiter-premium');
                } else {
                  setLocation('/job-seeker-premium');
                }
              }}
              className="w-full"
              size="sm"
            >
              <Crown className="h-4 w-4 mr-2" />
              Upgrade Now
            </Button>
          </CardContent>
        </Card>
      )}

      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="h-5 w-5" />
            Usage This Month
          </CardTitle>
          {report.subscription && (
            <div className="flex items-center gap-2">
              <Badge variant="default">
                {report.subscription.tierDetails?.name || 'Premium'}
              </Badge>
              {report.subscription.isActive && (
                <Badge variant="outline">
                  {report.subscription.daysRemaining} days left
                </Badge>
              )}
            </div>
          )}
        </CardHeader>
        <CardContent className="space-y-3">
          {Object.entries(report.usage).map(([key, used]) => {
            const limit = report.limits[key] || -1;
            const percentage = report.percentages[key] || 0;

            if (limit === 0) return null; // Skip features not available

            return (
              <div key={key} className="space-y-1">
                <div className="flex justify-between items-center text-sm">
                  <span>{formatUsageItem(key)}</span>
                  <span className={getUsageColor(percentage)}>
                    {used} / {formatLimit(limit)}
                  </span>
                </div>
                {limit > 0 && (
                  <div className="flex items-center gap-2">
                    <Progress 
                      value={percentage} 
                      className="flex-1 h-2"
                    />
                    <span className={`text-xs ${getUsageColor(percentage)}`}>
                      {percentage}%
                    </span>
                  </div>
                )}
              </div>
            );
          })}

          {isFreeTier && Object.keys(report.usage).length === 0 && (
            <div className="mt-4 p-3 bg-blue-50 dark:bg-blue-950 rounded-lg text-center">
              <div className="flex items-center justify-center gap-2 text-blue-800 dark:text-blue-200 mb-2">
                <Zap className="h-4 w-4" />
                <span className="font-medium">Start Using Features</span>
              </div>
              <p className="text-sm text-blue-700 dark:text-blue-300">
                Begin using AutoJobr features to see your usage statistics here.
              </p>
            </div>
          )}

          {isFreeTier && Object.keys(report.usage).length > 0 && (
            <div className="mt-4 p-3 bg-amber-50 dark:bg-amber-950 rounded-lg">
              <div className="flex items-center gap-2 text-amber-800 dark:text-amber-200 mb-2">
                <AlertTriangle className="h-4 w-4" />
                <span className="font-medium">Free Tier Active</span>
              </div>
              <p className="text-sm text-amber-700 dark:text-amber-300">
                You're using the free tier. Upgrade for unlimited access to all features.
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/applications-table.content:
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Skeleton } from "@/components/ui/skeleton";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Building, MapPin, MoreHorizontal, ExternalLink, Edit, Trash2, Zap, Globe } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { cn } from "@/lib/utils";

interface Application {
  id: string;
  jobTitle: string;
  company: string;
  location?: string;
  status: string;
  matchScore?: number;
  appliedDate: string;
  jobType?: string;
  workMode?: string;
  salaryRange?: string;
  jobUrl?: string;
  source?: 'internal' | 'extension';
  jobPostingId?: number;
}

interface ApplicationsTableProps {
  applications: Application[];
  isLoading: boolean;
  showActions?: boolean;
  onEdit?: (application: Application) => void;
  onDelete?: (application: Application) => void;
}

export function ApplicationsTable({ applications, isLoading, showActions = false, onEdit, onDelete }: ApplicationsTableProps) {
  if (isLoading) {
    return (
      <div className="space-y-4">
        {[...Array(5)].map((_, i) => (
          <div key={i} className="flex items-center space-x-4 p-4">
            <Skeleton className="h-10 w-10 rounded-lg" />
            <div className="space-y-2">
              <Skeleton className="h-4 w-32" />
              <Skeleton className="h-3 w-24" />
            </div>
            <div className="ml-auto">
              <Skeleton className="h-6 w-16" />
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (applications.length === 0) {
    return (
      <div className="text-center py-12">
        <Building className="w-12 h-12 text-muted-foreground mx-auto mb-4 opacity-50" />
        <h3 className="text-lg font-medium text-foreground mb-2">No applications yet</h3>
        <p className="text-muted-foreground">Start applying to jobs to see them here.</p>
      </div>
    );
  }

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      applied: { label: "Applied", className: "status-applied" },
      under_review: { label: "Under Review", className: "status-under-review" },
      interview: { label: "Interview", className: "status-interview" },
      offer: { label: "Offer", className: "status-offer" },
      rejected: { label: "Rejected", className: "status-rejected" },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || { 
      label: status, 
      className: "bg-secondary text-secondary-foreground" 
    };

    return (
      <Badge className={cn("inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium", config.className)}>
        {config.label}
      </Badge>
    );
  };

  const getMatchScoreColor = (score?: number) => {
    if (!score) return "bg-muted";
    if (score >= 90) return "bg-green-500";
    if (score >= 75) return "bg-blue-500";
    if (score >= 60) return "bg-amber-500";
    return "bg-red-500";
  };

  const getSourceBadge = (source?: 'internal' | 'extension') => {
    if (source === 'internal') {
      return (
        <Badge variant="outline" className="text-xs bg-blue-50 text-blue-700 border-blue-200 dark:bg-blue-950 dark:text-blue-300 dark:border-blue-800">
          <Zap className="w-3 h-3 mr-1" />
          Platform
        </Badge>
      );
    }
    if (source === 'extension') {
      return (
        <Badge variant="outline" className="text-xs bg-green-50 text-green-700 border-green-200 dark:bg-green-950 dark:text-green-300 dark:border-green-800">
          <Globe className="w-3 h-3 mr-1" />
          Extension
        </Badge>
      );
    }
    return null;
  };

  return (
    <>
      {/* Mobile Card Layout */}
      <div className="block md:hidden space-y-4">
        {applications.map((application) => (
          <div key={application.id} className="bg-card border border-border rounded-lg p-4 space-y-3">
            <div className="flex items-start justify-between">
              <div className="flex-1">
                <h3 className="font-medium text-foreground text-sm">{application.jobTitle}</h3>
                <div className="flex items-center text-muted-foreground text-xs mt-1">
                  <Building className="w-3 h-3 mr-1" />
                  <span>{application.company}</span>
                  {application.location && (
                    <>
                      <span className="mx-1">â€¢</span>
                      <MapPin className="w-3 h-3 mr-1" />
                      <span>{application.location}</span>
                    </>
                  )}
                </div>
              </div>
              {showActions && (
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" size="sm" className="h-8 w-8 p-0">
                      <MoreHorizontal className="h-4 w-4" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end">
                    {application.jobUrl && (
                      <DropdownMenuItem asChild>
                        <a href={application.jobUrl} target="_blank" rel="noopener noreferrer">
                          <ExternalLink className="mr-2 h-4 w-4" />
                          View Job
                        </a>
                      </DropdownMenuItem>
                    )}
                    <DropdownMenuItem>
                      <Edit className="mr-2 h-4 w-4" />
                      Edit
                    </DropdownMenuItem>
                    <DropdownMenuItem className="text-destructive">
                      <Trash2 className="mr-2 h-4 w-4" />
                      Delete
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              )}
            </div>

            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2 flex-wrap">
                {getStatusBadge(application.status)}
                {getSourceBadge(application.source)}
                {application.matchScore && (
                  <div className="flex items-center space-x-1">
                    <div className="w-12 bg-muted rounded-full h-2">
                      <div 
                        className={`h-2 rounded-full ${getMatchScoreColor(application.matchScore)}`}
                        style={{ width: `${application.matchScore}%` }}
                      />
                    </div>
                    <span className="text-xs text-muted-foreground">{application.matchScore}%</span>
                  </div>
                )}
              </div>
              <span className="text-xs text-muted-foreground">
                {formatDistanceToNow(new Date(application.appliedDate), { addSuffix: true })}
              </span>
            </div>
          </div>
        ))}
      </div>

      {/* Desktop Table Layout */}
      <div className="hidden md:block overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="border-b border-border">
              <th className="text-left text-xs font-medium text-muted-foreground uppercase tracking-wider py-3 px-6">
                Company
              </th>
              <th className="text-left text-xs font-medium text-muted-foreground uppercase tracking-wider py-3 px-6">
                Position
              </th>
              <th className="text-left text-xs font-medium text-muted-foreground uppercase tracking-wider py-3 px-6">
                Match
              </th>
              <th className="text-left text-xs font-medium text-muted-foreground uppercase tracking-wider py-3 px-6">
                Status
              </th>
              <th className="text-left text-xs font-medium text-muted-foreground uppercase tracking-wider py-3 px-6">
                Source
              </th>
              <th className="text-left text-xs font-medium text-muted-foreground uppercase tracking-wider py-3 px-6">
                Applied
              </th>
              {showActions && (
                <th className="text-left text-xs font-medium text-muted-foreground uppercase tracking-wider py-3 px-6">
                  Actions
                </th>
              )}
            </tr>
          </thead>
        <tbody className="divide-y divide-border">
          {applications.map((application) => (
            <tr key={application.id} className="hover:bg-muted/50 transition-colors">
              <td className="py-4 px-6 whitespace-nowrap">
                <div className="flex items-center">
                  <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center mr-3">
                    <Building className="w-4 h-4 text-primary" />
                  </div>
                  <div>
                    <div className="text-sm font-medium text-foreground">
                      {application.company}
                    </div>
                    {application.location && (
                      <div className="text-sm text-muted-foreground flex items-center">
                        <MapPin className="w-3 h-3 mr-1" />
                        {application.location}
                      </div>
                    )}
                  </div>
                </div>
              </td>
              <td className="py-4 px-6 whitespace-nowrap">
                <div className="text-sm font-medium text-foreground">
                  {application.jobTitle}
                </div>
                <div className="text-sm text-muted-foreground">
                  {application.jobType && `${application.jobType} â€¢ `}
                  {application.workMode}
                </div>
              </td>
              <td className="py-4 px-6 whitespace-nowrap">
                {application.matchScore ? (
                  <div className="flex items-center">
                    <div className="w-16 bg-muted rounded-full h-2 mr-2">
                      <div 
                        className={`h-2 rounded-full ${getMatchScoreColor(application.matchScore)}`}
                        style={{ width: `${application.matchScore}%` }}
                      />
                    </div>
                    <span className="text-sm font-medium text-foreground">
                      {application.matchScore}%
                    </span>
                  </div>
                ) : (
                  <span className="text-sm text-muted-foreground">-</span>
                )}
              </td>
              <td className="py-4 px-6 whitespace-nowrap">
                {getStatusBadge(application.status)}
              </td>
              <td className="py-4 px-6 whitespace-nowrap">
                {getSourceBadge(application.source)}
              </td>
              <td className="py-4 px-6 whitespace-nowrap text-sm text-muted-foreground">
                {formatDistanceToNow(new Date(application.appliedDate), { addSuffix: true })}
              </td>
              {showActions && (
                <td className="py-4 px-6 whitespace-nowrap">
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button variant="ghost" size="icon">
                        <MoreHorizontal className="w-4 h-4" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      {application.jobUrl && (
                        <DropdownMenuItem>
                          <ExternalLink className="w-4 h-4 mr-2" />
                          View Job
                        </DropdownMenuItem>
                      )}
                      <DropdownMenuItem onClick={() => onEdit?.(application)}>
                        <Edit className="w-4 h-4 mr-2" />
                        Edit
                      </DropdownMenuItem>
                      <DropdownMenuItem 
                        className="text-destructive"
                        onClick={() => onDelete?.(application)}
                      >
                        <Trash2 className="w-4 h-4 mr-2" />
                        Delete
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </td>
              )}
            </tr>
          ))}
        </tbody>
        </table>
      </div>
    </>
  );
}

client/src/components/job-card.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Building, MapPin, Briefcase, DollarSign, Bookmark, ExternalLink, Clock } from "lucide-react";
import { cn } from "@/lib/utils";

interface JobCardProps {
  job: {
    id: number;
    jobTitle: string;
    company: string;
    location?: string;
    jobUrl?: string;
    salary?: string;
    jobType?: string;
    workMode?: string;
    matchScore?: number;
    matchingSkills?: string[];
    missingSkills?: string[];
    jobDescription?: string;
    isBookmarked?: boolean;
    isApplied?: boolean;
  };
}

export function JobCard({ job }: JobCardProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isBookmarked, setIsBookmarked] = useState(job.isBookmarked || false);

  const bookmarkMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("PATCH", `/api/recommendations/${job.id}/bookmark`);
    },
    onSuccess: () => {
      setIsBookmarked(!isBookmarked);
      queryClient.invalidateQueries({ queryKey: ["/api/recommendations"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update bookmark",
        variant: "destructive",
      });
    },
  });

  const getMatchScoreColor = (score?: number) => {
    if (!score) return "match-score-poor";
    if (score >= 90) return "match-score-excellent";
    if (score >= 75) return "match-score-good";
    if (score >= 60) return "match-score-fair";
    return "match-score-poor";
  };

  const getMatchScoreText = (score?: number) => {
    if (!score) return "No Match";
    return `${score}% Match`;
  };

  const handleBookmark = () => {
    bookmarkMutation.mutate();
  };

  const handleApply = () => {
    if (job.jobUrl) {
      window.open(job.jobUrl, "_blank");
    }
  };

  return (
    <Card className="hover:shadow-md transition-shadow">
      <CardContent className="p-4 sm:p-6">
        <div className="flex items-start justify-between mb-3 sm:mb-4">
          <div className="flex items-center space-x-2 sm:space-x-3 flex-1 min-w-0">
            <div className="w-10 h-10 sm:w-12 sm:h-12 bg-primary/10 rounded-lg flex items-center justify-center flex-shrink-0">
              <Building className="w-5 h-5 sm:w-6 sm:h-6 text-primary" />
            </div>
            <div className="min-w-0 flex-1">
              <h3 className="font-semibold text-foreground text-sm sm:text-base truncate">{job.company}</h3>
              <p className="text-xs sm:text-sm text-muted-foreground flex items-center">
                {job.location && (
                  <>
                    <MapPin className="w-3 h-3 mr-1 flex-shrink-0" />
                    <span className="truncate">{job.location}</span>
                  </>
                )}
              </p>
            </div>
          </div>
          <div className={cn(
            "text-xs sm:text-sm font-semibold px-2 py-1 rounded whitespace-nowrap ml-2",
            getMatchScoreColor(job.matchScore)
          )}>
            {getMatchScoreText(job.matchScore)}
          </div>
        </div>

        <h4 className="text-base sm:text-lg font-semibold text-foreground mb-2">
          {job.jobTitle}
        </h4>

        {job.jobDescription && (
          <p className="text-sm text-muted-foreground mb-4 line-clamp-2">
            {job.jobDescription}
          </p>
        )}

        <div className="flex items-center justify-between text-sm text-muted-foreground mb-4">
          {job.jobType && (
            <span className="flex items-center">
              <Briefcase className="w-3 h-3 mr-1" />
              {job.jobType}
            </span>
          )}
          {job.workMode && (
            <span className="flex items-center">
              <MapPin className="w-3 h-3 mr-1" />
              {job.workMode}
            </span>
          )}
          {job.salary && (
            <span className="flex items-center">
              <DollarSign className="w-3 h-3 mr-1" />
              {job.salary}
            </span>
          )}
        </div>

        {(job.matchingSkills?.length || job.missingSkills?.length) && (
          <div className="space-y-2 mb-4">
            {job.matchingSkills && job.matchingSkills.length > 0 && (
              <div className="flex flex-wrap gap-1">
                {job.matchingSkills.slice(0, 3).map((skill, index) => (
                  <Badge key={index} variant="secondary" className="skill-tag text-xs">
                    {skill}
                  </Badge>
                ))}
                {job.matchingSkills.length > 3 && (
                  <Badge variant="secondary" className="text-xs">
                    +{job.matchingSkills.length - 3} more
                  </Badge>
                )}
              </div>
            )}
            {job.missingSkills && job.missingSkills.length > 0 && (
              <div className="flex flex-wrap gap-1">
                {job.missingSkills.slice(0, 2).map((skill, index) => (
                  <Badge key={index} variant="outline" className="text-xs text-amber-600 border-amber-200">
                    {skill}
                  </Badge>
                ))}
                {job.missingSkills.length > 2 && (
                  <Badge variant="outline" className="text-xs text-amber-600 border-amber-200">
                    +{job.missingSkills.length - 2} missing
                  </Badge>
                )}
              </div>
            )}
          </div>
        )}

        <div className="flex space-x-2">
          <Button 
            className="flex-1 text-sm sm:text-base" 
            onClick={handleApply}
            disabled={job.isApplied}
            size="sm"
          >
            {job.isApplied ? (
              <>
                <Clock className="w-4 h-4 mr-1 sm:mr-2" />
                <span className="hidden sm:inline">Applied</span>
                <span className="sm:hidden">Done</span>
              </>
            ) : (
              <>
                <ExternalLink className="w-4 h-4 mr-1 sm:mr-2" />
                <span className="hidden sm:inline">Apply Now</span>
                <span className="sm:hidden">Apply</span>
              </>
            )}
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={handleBookmark}
            disabled={bookmarkMutation.isPending}
            className={cn(
              isBookmarked && "bg-primary/10 border-primary text-primary"
            )}
          >
            <Bookmark className={cn("w-4 h-4", isBookmarked && "fill-current")} />
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/mock-interview-pay.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { 
  CreditCard, 
  CheckCircle, 
  AlertCircle, 
  Loader2,
  Shield,
  Star,
  MessageSquare,
  Brain,
  Trophy,
  DollarSign
} from "lucide-react";

interface MockInterviewPaymentProps {
  onSuccess?: () => void;
  onCancel?: () => void;
}

export function MockInterviewPayment({ onSuccess, onCancel }: MockInterviewPaymentProps) {
  const { toast } = useToast();
  const [paymentMethod, setPaymentMethod] = useState<'stripe' | 'razorpay' | 'paypal'>('stripe');
  const [isProcessing, setIsProcessing] = useState(false);

  // Create payment intent mutation
  const createPaymentMutation = useMutation({
    mutationFn: async (method: string) => {
      const response = await apiRequest('POST', '/api/mock-interview/payment', {
        amount: 2.00,
        currency: 'USD',
        method: method,
        item: 'mock_interview'
      });
      return response;
    },
    onSuccess: (data) => {
      toast({
        title: "Payment Successful!",
        description: "You can now start your mock interview.",
        duration: 3000,
      });
      onSuccess?.();
    },
    onError: (error: any) => {
      toast({
        title: "Payment Failed",
        description: error.message || "There was an error processing your payment.",
        variant: "destructive",
      });
      setIsProcessing(false);
    },
  });

  const handlePayment = async () => {
    setIsProcessing(true);

    try {
      if (paymentMethod === 'stripe') {
        // Handle Stripe payment
        const response = await createPaymentMutation.mutateAsync('stripe');
        if (response.clientSecret) {
          // Redirect to Stripe checkout or handle client-side Stripe
          window.location.href = `/payment/stripe?client_secret=${response.clientSecret}&type=mock_interview`;
        }
      } else if (paymentMethod === 'razorpay') {
        // Handle Razorpay payment
        const response = await createPaymentMutation.mutateAsync('razorpay');
        if (response.orderId) {
          // Initialize Razorpay checkout
          const options = {
            key: response.keyId,
            amount: response.amount,
            currency: response.currency,
            name: 'AutoJobr',
            description: 'Mock Interview Practice',
            order_id: response.orderId,
            handler: function (response: any) {
              toast({
                title: "Payment Successful!",
                description: "You can now start your mock interview.",
              });
              onSuccess?.();
            },
            prefill: {
              email: response.email,
              contact: response.phone
            },
            theme: {
              color: '#3B82F6'
            }
          };
          const rzp = new (window as any).Razorpay(options);
          rzp.open();
        }
      } else if (paymentMethod === 'paypal') {
        // Handle PayPal payment
        const response = await createPaymentMutation.mutateAsync('paypal');
        if (response.approvalUrl) {
          // Redirect to PayPal
          window.location.href = response.approvalUrl;
        }
      }
    } catch (error) {
      console.error('Payment error:', error);
      setIsProcessing(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white dark:bg-gray-800 rounded-lg p-6 max-w-md w-full mx-4 max-h-[90vh] overflow-y-auto">
        <div className="text-center mb-6">
          <div className="p-3 bg-blue-100 dark:bg-blue-900 rounded-full w-16 h-16 mx-auto mb-4">
            <MessageSquare className="w-10 h-10 text-blue-600 dark:text-blue-400" />
          </div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-gray-100 mb-2">
            Mock Interview Practice
          </h2>
          <p className="text-gray-600 dark:text-gray-400">
            Unlock unlimited mock interview practice sessions
          </p>
        </div>

        {/* Features */}
        <div className="space-y-3 mb-6">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-green-100 dark:bg-green-900 rounded-full">
              <Brain className="w-4 h-4 text-green-600 dark:text-green-400" />
            </div>
            <div>
              <h3 className="font-semibold text-gray-900 dark:text-gray-100">AI-Powered Questions</h3>
              <p className="text-sm text-gray-600 dark:text-gray-400">Dynamic technical and behavioral questions</p>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <div className="p-2 bg-blue-100 dark:bg-blue-900 rounded-full">
              <Trophy className="w-4 h-4 text-blue-600 dark:text-blue-400" />
            </div>
            <div>
              <h3 className="font-semibold text-gray-900 dark:text-gray-100">Real-time Feedback</h3>
              <p className="text-sm text-gray-600 dark:text-gray-400">Instant scoring and improvement suggestions</p>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <div className="p-2 bg-purple-100 dark:bg-purple-900 rounded-full">
              <MessageSquare className="w-4 h-4 text-purple-600 dark:text-purple-400" />
            </div>
            <div>
              <h3 className="font-semibold text-gray-900 dark:text-gray-100">Multiple Formats</h3>
              <p className="text-sm text-gray-600 dark:text-gray-400">Technical, behavioral, and system design</p>
            </div>
          </div>
        </div>

        {/* Pricing */}
        <div className="bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 rounded-lg p-4 mb-6">
          <div className="flex items-center justify-between">
            <div>
              <div className="flex items-center gap-2">
                <DollarSign className="w-5 h-5 text-blue-600" />
                <span className="text-2xl font-bold text-gray-900 dark:text-gray-100">$2.00</span>
                <span className="text-sm text-gray-600 dark:text-gray-400">per interview</span>
              </div>
              <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
                Or get unlimited access with Premium
              </p>
            </div>
            <Badge className="bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200">
              One-time
            </Badge>
          </div>
        </div>

        {/* Payment Method Selection */}
        <Tabs value={paymentMethod} onValueChange={(value: any) => setPaymentMethod(value)} className="mb-6">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="stripe">Stripe</TabsTrigger>
            <TabsTrigger value="razorpay">Razorpay</TabsTrigger>
            <TabsTrigger value="paypal">PayPal</TabsTrigger>
          </TabsList>

          <TabsContent value="stripe" className="space-y-2">
            <div className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400">
              <Shield className="w-4 h-4" />
              <span>Secure payment with Stripe</span>
            </div>
            <p className="text-xs text-gray-500 dark:text-gray-400">
              Supports all major credit cards, Apple Pay, Google Pay
            </p>
          </TabsContent>

          <TabsContent value="razorpay" className="space-y-2">
            <div className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400">
              <Shield className="w-4 h-4" />
              <span>Pay with Razorpay</span>
            </div>
            <p className="text-xs text-gray-500 dark:text-gray-400">
              UPI, Net Banking, Cards, and Wallet payments
            </p>
          </TabsContent>

          <TabsContent value="paypal" className="space-y-2">
            <div className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400">
              <Shield className="w-4 h-4" />
              <span>Pay with PayPal</span>
            </div>
            <p className="text-xs text-gray-500 dark:text-gray-400">
              PayPal balance, bank account, or credit card
            </p>
          </TabsContent>
        </Tabs>

        {/* Security Notice */}
        <div className="bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 rounded-lg p-3 mb-6">
          <div className="flex items-center gap-2 text-green-800 dark:text-green-200">
            <Shield className="w-4 h-4" />
            <span className="text-sm font-medium">Secure & Encrypted</span>
          </div>
          <p className="text-xs text-green-700 dark:text-green-300 mt-1">
            All payments are processed securely. We don't store your payment information.
          </p>
        </div>

        {/* Action Buttons */}
        <div className="flex gap-3">
          <Button 
            variant="outline" 
            onClick={onCancel}
            disabled={isProcessing}
            className="flex-1"
          >
            Cancel
          </Button>
          <Button 
            onClick={handlePayment}
            disabled={isProcessing}
            className="flex-1 bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700"
          >
            {isProcessing ? (
              <div className="flex items-center gap-2">
                <Loader2 className="w-4 h-4 animate-spin" />
                Processing...
              </div>
            ) : (
              <div className="flex items-center gap-2">
                <CreditCard className="w-4 h-4" />
                Pay $2.00
              </div>
            )}
          </Button>
        </div>

        {/* Alternative Option */}
        <div className="text-center mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
          <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
            Want unlimited interviews?
          </p>
          <Button 
            variant="link" 
            onClick={() => window.open('/subscription', '_blank')}
            className="text-blue-600 hover:text-blue-700"
          >
            <Star className="w-4 h-4 mr-1" />
            Upgrade to Premium
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/navbar.tsx.content:
import { useState } from "react";
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useTheme } from "@/components/theme-provider";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { UserAvatar } from "@/components/profile-avatar";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuSeparator, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Rocket, Moon, Sun, User, Settings, LogOut, BarChart3, FileText, Briefcase, Crown, Menu, X, Plus, MessageCircle, Target, Brain, Users, Trophy, Code, Bell, Upload, Zap, HelpCircle, ChevronDown, TrendingUp } from "lucide-react";
import { cn } from "@/lib/utils";

export function Navbar() {
  const [location] = useLocation();
  const { user } = useAuth() as { user: any };
  const { theme, setTheme } = useTheme();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Get unread message count
  const { data: conversations } = useQuery({
    queryKey: ['/api/chat/conversations'],
    enabled: !!user?.id,
    refetchInterval: 30000 // Refresh every 30 seconds
  });

  const totalUnreadCount = conversations?.reduce((total: number, conv: any) => 
    total + (conv.unreadCount || 0), 0) || 0;

  const handleLogout = async () => {
    try {
      const response = await fetch('/api/auth/signout', {
        method: 'POST',
        credentials: 'include'
      });

      if (response.ok) {
        // Redirect to landing page after successful logout
        window.location.href = '/';
      }
    } catch (error) {
      console.error('Logout failed:', error);
      // Fallback: still redirect to landing page
      window.location.href = '/';
    }
  };

  // Define navigation items based on user type
  const getNavItems = () => {
    if (!user) {
      // For non-authenticated users
      return [
        { href: "/recruiter-features", label: "For Recruiters", icon: Users },
      ];
    } else if (user?.userType === 'recruiter' || user?.userType === 'company') {
      return [
        { href: "/", label: "Dashboard", icon: BarChart3 },
        { href: "/post-job", label: "Post Job", icon: Plus },
        { href: "/test-assignments", label: "Test Assignments", icon: FileText },
        { href: "/profile", label: "Profile", icon: User },
        { href: "/chat", label: "Messages", icon: MessageCircle },
        { href: "/job-seeker-view", label: "View as Job Seeker", icon: Users },
        { href: "/recruiter-premium", label: "ðŸš€ Upgrade", icon: Crown, premium: true },
      ];
    } else {
      return [
        { href: "/", label: "Dashboard", icon: BarChart3 },
        { href: "/applications", label: "Applications", icon: FileText },
        { href: "/jobs", label: "Jobs", icon: Briefcase },
        { href: "/post-job", label: "Post Job", icon: Plus },
        { href: "/job-seeker-tests", label: "Tests", icon: FileText },
        { href: "/ranking-tests", label: "Rankings", icon: Trophy },
        { href: "/mock-interview", label: "Practice", icon: Code },
        { href: "/chat", label: "Messages", icon: MessageCircle },
        { href: "/profile", label: "Profile", icon: User },
        { href: "/job-seeker-premium", label: "ðŸš€ Upgrade", icon: Crown, premium: true },
      ];
    }
  };

  const navItems = getNavItems();

  return (
    <nav className="bg-background/95 backdrop-blur-md border-b border-border/50 sticky top-0 z-50 w-full shadow-sm">
      <div className="w-full mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center space-x-2 sm:space-x-4">
            <Link href="/" className="flex items-center space-x-2 group">
              <div className="w-8 h-8 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center group-hover:scale-110 transition-transform duration-200">
                <Rocket className="w-4 h-4 text-white" />
              </div>
              <span className="text-lg sm:text-xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                AutoJobr
              </span>
              {user?.planType === 'premium' && (
                <Crown className="w-4 h-4 text-yellow-500" />
              )}
            </Link>
            <div className="hidden md:flex space-x-2 lg:space-x-4">
              {navItems.map((item) => {
                const Icon = item.icon;
                const isActive = location === item.href;
                return (
                  <Link key={item.href} href={item.href}>
                    <button
                      className={cn(
                        "flex items-center space-x-2 text-sm font-medium px-4 py-2 rounded-lg transition-all duration-200 hover:scale-105",
                        item.premium 
                          ? "bg-gradient-to-r from-purple-500 to-pink-500 text-white font-semibold hover:from-purple-600 hover:to-pink-600 shadow-lg hover:shadow-xl" 
                          : isActive
                          ? "text-primary bg-primary/15 shadow-sm border border-primary/20"
                          : "text-muted-foreground hover:text-foreground hover:bg-muted/60"
                      )}
                    >
                      <Icon className={cn("w-4 h-4", item.premium && "text-yellow-200")} />
                      <span className="hidden lg:inline">{item.label}</span>
                    </button>
                  </Link>
                );
              })}
            </div>
          </div>

          <div className="flex items-center space-x-3">
            {/* Notifications Bell - only for authenticated users */}
            {user && (
              <Link href="/chat">
                <Button
                  variant="ghost"
                  size="sm"
                  className="hidden sm:flex h-9 w-9 rounded-full hover:bg-muted/80 transition-all duration-200 relative"
                >
                  <Bell className="h-4 w-4" />
                  {totalUnreadCount > 0 && (
                    <span className="absolute -top-1 -right-1 h-3 w-3 bg-red-500 rounded-full text-[10px] text-white flex items-center justify-center">
                      {totalUnreadCount > 9 ? '9+' : totalUnreadCount}
                    </span>
                  )}
                  <span className="sr-only">Messages</span>
                </Button>
              </Link>
            )}

            {/* Theme toggle with improved styling */}
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
              className="hidden sm:flex h-9 w-9 rounded-full hover:bg-muted/80 transition-all duration-200"
            >
              <Sun className="h-4 w-4 rotate-0 scale-100 transition-all dark:-rotate-90 dark:scale-0" />
              <Moon className="absolute h-4 w-4 rotate-90 scale-0 transition-all dark:rotate-0 dark:scale-100" />
              <span className="sr-only">Toggle theme</span>
            </Button>

            {/* Mobile menu button */}
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              className="md:hidden"
            >
              {isMobileMenuOpen ? (
                <X className="h-5 w-5" />
              ) : (
                <Menu className="h-5 w-5" />
              )}
              <span className="sr-only">Toggle menu</span>
            </Button>

            {/* Login button for non-authenticated users */}
            {!user && (
              <Button 
                onClick={() => window.location.href = "/auth"} 
                className="bg-primary hover:bg-primary/90"
              >
                Sign In
              </Button>
            )}

            {/* User dropdown for authenticated users */}
            {user && (
              <DropdownMenu>
                <DropdownMenuTrigger asChild className="hidden md:flex">
                  <Button variant="ghost" className="relative h-10 w-10 rounded-full hover:bg-muted/80 transition-all duration-200 ring-2 ring-transparent hover:ring-primary/20">
                    <UserAvatar 
                      user={{
                        id: user?.id || '',
                        email: user?.email || '',
                        firstName: user?.firstName,
                        lastName: user?.lastName,
                        profileImageUrl: user?.profileImageUrl
                      }} 
                      size="sm" 
                    />
                  </Button>
                </DropdownMenuTrigger>
              <DropdownMenuContent className="w-56" align="end" forceMount>
                <div className="flex items-center justify-start gap-2 p-2">
                  <div className="flex flex-col space-y-1 leading-none">
                    <p className="font-medium">
                      {user?.firstName && user?.lastName 
                        ? `${user.firstName} ${user.lastName}` 
                        : user?.name || user?.email?.split('@')[0] || 'User'
                      }
                    </p>
                    <p className="w-[200px] truncate text-sm text-muted-foreground">
                      {user?.email}
                    </p>
                  </div>
                </div>
                <DropdownMenuSeparator />
                <Link href="/profile">
                  <DropdownMenuItem>
                    <User className="mr-2 h-4 w-4" />
                    <span>Profile</span>
                  </DropdownMenuItem>
                </Link>
                <Link href="/subscription">
                  <DropdownMenuItem>
                    <Crown className="mr-2 h-4 w-4" />
                    <span>Subscription</span>
                  </DropdownMenuItem>
                </Link>
                <DropdownMenuItem>
                  <Settings className="mr-2 h-4 w-4" />
                  <span>Settings</span>
                </DropdownMenuItem>
                <DropdownMenuItem>
                  <HelpCircle className="mr-2 h-4 w-4" />
                  <span>Help & Support</span>
                </DropdownMenuItem>

                <DropdownMenuSeparator />
                <DropdownMenuItem onClick={handleLogout}>
                  <LogOut className="mr-2 h-4 w-4" />
                  <span>Log out</span>
                </DropdownMenuItem>
              </DropdownMenuContent>
              </DropdownMenu>
            )}
          </div>
        </div>

        {/* Mobile Navigation Menu */}
        {isMobileMenuOpen && (
          <div className="md:hidden border-t border-border/50 bg-background/98 backdrop-blur-md shadow-lg">
