      benefits: [
        { icon: <Bot className="w-4 h-4" />, text: "Full AI analysis suite" },
        { icon: <FileText className="w-4 h-4" />, text: "Advanced resume tools" },
        { icon: <Target className="w-4 h-4" />, text: "Enhanced job matching" },
