import { useState, useEffect, useRef } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { 
  MessageCircle, 
  Clock, 
  Send, 
  Bot, 
  User, 
  CheckCircle, 
  XCircle,
  Play,
  Pause,
  Square,
  TrendingUp,
  Award,
  Brain,
  Target
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface VirtualInterviewMessage {
  id: number;
  sender: 'interviewer' | 'candidate';
  messageType: 'text' | 'question' | 'answer' | 'feedback' | 'system';
  content: string;
  timestamp: string;
  messageIndex: number;
  responseQuality?: number;
  technicalAccuracy?: number;
  clarityScore?: number;
  sentiment?: string;
}

interface VirtualInterview {
  id: number;
  sessionId: string;
  interviewType: string;
  role: string;
  company?: string;
  difficulty: string;
  duration: number;
  status: string;
  currentStep: string;
  questionsAsked: number;
  totalQuestions: number;
  timeRemaining: number;
  overallScore?: number;
  technicalScore?: number;
  communicationScore?: number;
  confidenceScore?: number;
}

export default function VirtualInterview() {
  const { sessionId } = useParams<{ sessionId: string }>();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [currentMessage, setCurrentMessage] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [timeLeft, setTimeLeft] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Add type safety for sessionData
  interface SessionData {
    interview: VirtualInterview;
    messages: VirtualInterviewMessage[];
  }

  // Fetch interview session data
  const { data: sessionData, isLoading } = useQuery<SessionData>({
    queryKey: [`/api/virtual-interview/${sessionId}`],
    enabled: !!sessionId,
  });

  const interview: VirtualInterview | undefined = sessionData?.interview;
  const messages: VirtualInterviewMessage[] = sessionData?.messages || [];

  // Start interview mutation for assigned interviews
  const startInterviewMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest(`/api/virtual-interview/${sessionId}/start`, 'POST');
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/virtual-interview/${sessionId}`] });
      toast({
        title: "Interview Started!",
        description: "Your virtual interview has begun. Good luck!",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to start interview",
        variant: "destructive",
      });
    },
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (data: { content: string; messageType: string }) => {
      return await apiRequest(`/api/virtual-interview/${sessionId}/message`, 'POST', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/virtual-interview/${sessionId}`] });
      setCurrentMessage("");
      setIsTyping(false);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to send message",
        variant: "destructive",
      });
      setIsTyping(false);
    },
  });

  // Complete interview mutation
  const completeInterviewMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest(`/api/virtual-interview/${sessionId}/complete`, 'POST');
    },
    onSuccess: (data) => {
      toast({
        title: "Interview Complete!",
        description: "Your detailed feedback is ready.",
      });
      setLocation(`/virtual-interview/${sessionId}/feedback`);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to complete interview",
        variant: "destructive",
      });
    },
  });

  // Timer effect
  useEffect(() => {
    if (interview && interview.status === 'active' && !isPaused) {
      setTimeLeft(interview.timeRemaining);

      const timer = setInterval(() => {
        setTimeLeft(prev => {
          const newTime = Math.max(0, prev - 1);
          if (newTime <= 0) {
            completeInterviewMutation.mutate();
            return 0;
          }
          return newTime;
        });
      }, 1000);

      return () => clearInterval(timer);
    } else if (interview) {
      setTimeLeft(interview.timeRemaining);
    }
  }, [interview, isPaused]);

  // OPTIMIZATION: Reduce sync frequency and use conditional updates
  useEffect(() => {
    if (interview && interview.status === 'active') {
      const syncTimer = setInterval(() => {
        // Only sync if window is visible and user is active
        if (!document.hidden) {
          queryClient.invalidateQueries({ queryKey: [`/api/virtual-interview/${sessionId}`] });
        }
      }, 30000);

      return () => clearInterval(syncTimer);
    }
  }, [interview, sessionId]);

  // Scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Focus input when component mounts
  useEffect(() => {
    inputRef.current?.focus();
  }, []);

  const handleSendMessage = async () => {
    if (!currentMessage.trim() || isTyping) return;

    setIsTyping(true);

    // Determine message type based on last interviewer message
    const lastInterviewerMessage = messages
      .filter(m => m.sender === 'interviewer')
      .slice(-1)[0];

    const messageType = lastInterviewerMessage?.messageType === 'question' ? 'answer' : 'text';

    sendMessageMutation.mutate({
      content: currentMessage.trim(),
      messageType
    });
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getMessageIcon = (sender: string, messageType?: string) => {
    if (sender === 'interviewer') {
      return <Bot className="h-4 w-4 text-blue-500" />;
    }
    return <User className="h-4 w-4 text-green-500" />;
  };

  const getScoreColor = (score?: number) => {
    if (!score) return "text-gray-500";
    if (score >= 80) return "text-green-500";
    if (score >= 60) return "text-yellow-500";
    return "text-red-500";
  };

  const getScoreBadge = (score?: number) => {
    if (!score) return null;
    if (score >= 80) return <Badge variant="default" className="bg-green-500">Excellent</Badge>;
    if (score >= 60) return <Badge variant="default" className="bg-yellow-500">Good</Badge>;
    return <Badge variant="destructive">Needs Work</Badge>;
  };

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex items-center justify-center min-h-[400px]">
          <div className="text-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-500 mx-auto"></div>
            <p className="mt-4 text-lg">Loading your interview session...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!interview) {
    return (
      <div className="container mx-auto px-4 py-8">
        <Card>
          <CardContent className="pt-6">
            <div className="text-center">
              <XCircle className="h-16 w-16 text-red-500 mx-auto mb-4" />
              <h2 className="text-2xl font-bold mb-2">Interview Session Not Found</h2>
              <p className="text-gray-600 mb-4">The interview session you're looking for doesn't exist or has expired.</p>
              <Button onClick={() => setLocation('/virtual-interview/new')}>
                Start New Interview
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  const progress = (interview.questionsAsked / interview.totalQuestions) * 100;

  return (
    <div className="container mx-auto px-4 py-6 max-w-6xl">
      <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">

        {/* Interview Info Sidebar */}
        <div className="lg:col-span-1">
          <Card className="sticky top-6">
            <CardHeader>
              <CardTitle className="text-lg flex items-center gap-2">
                <Target className="h-5 w-5" />
                Interview Details
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <h3 className="font-semibold text-sm text-gray-600 uppercase tracking-wide">Role</h3>
                <p className="text-lg font-medium">{interview.role.replace(/_/g, ' ')}</p>
                {interview.company && (
                  <p className="text-sm text-gray-600">at {interview.company}</p>
                )}
              </div>

              <div>
                <h3 className="font-semibold text-sm text-gray-600 uppercase tracking-wide">Type & Difficulty</h3>
                <div className="flex gap-2 mt-1">
                  <Badge variant="outline">{interview.interviewType}</Badge>
                  <Badge variant={interview.difficulty === 'hard' ? 'destructive' : interview.difficulty === 'medium' ? 'default' : 'secondary'}>
                    {interview.difficulty}
                  </Badge>
                </div>
              </div>

              <div>
                <h3 className="font-semibold text-sm text-gray-600 uppercase tracking-wide mb-2">Progress</h3>
                <Progress value={progress} className="mb-2" />
                <p className="text-sm text-gray-600">
                  Question {interview.questionsAsked} of {interview.totalQuestions}
                </p>
              </div>

              <div>
                <h3 className="font-semibold text-sm text-gray-600 uppercase tracking-wide flex items-center gap-1">
                  <Clock className="h-4 w-4" />
                  Time Remaining
                </h3>
                <p className="text-2xl font-mono font-bold mt-1">
                  {formatTime(timeLeft)}
                </p>
              </div>

              {(interview.technicalScore || interview.communicationScore || interview.confidenceScore) && (
                <div>
                  <h3 className="font-semibold text-sm text-gray-600 uppercase tracking-wide mb-2">Live Scores</h3>
                  <div className="space-y-2">
                    {interview.technicalScore && (
                      <div className="flex justify-between items-center">
                        <span className="text-sm">Technical</span>
                        <span className={`font-bold ${getScoreColor(interview.technicalScore)}`}>
                          {interview.technicalScore}%
                        </span>
                      </div>
                    )}
                    {interview.communicationScore && (
                      <div className="flex justify-between items-center">
                        <span className="text-sm">Communication</span>
                        <span className={`font-bold ${getScoreColor(interview.communicationScore)}`}>
                          {interview.communicationScore}%
                        </span>
                      </div>
                    )}
                    {interview.confidenceScore && (
                      <div className="flex justify-between items-center">
                        <span className="text-sm">Confidence</span>
                        <span className={`font-bold ${getScoreColor(interview.confidenceScore)}`}>
                          {interview.confidenceScore}%
                        </span>
                      </div>
                    )}
                  </div>
                </div>
              )}

              <div className="pt-4">
                {interview.status === 'assigned' && (
                  <Button 
                    onClick={() => startInterviewMutation.mutate()}
                    className="w-full"
                    disabled={startInterviewMutation.isPending}
                  >
                    <Play className="h-4 w-4 mr-2" />
                    Start Interview
                  </Button>
                )}
                {interview.status === 'active' && (
                  <Button 
                    variant="outline" 
                    onClick={() => completeInterviewMutation.mutate()}
                    className="w-full"
                    disabled={completeInterviewMutation.isPending}
                  >
                    <Square className="h-4 w-4 mr-2" />
                    End Interview
                  </Button>
                )}
                {interview.status === 'completed' && (
                  <Button 
                    onClick={() => setLocation(`/virtual-interview/${sessionId}/feedback`)}
                    className="w-full"
                  >
                    <Award className="h-4 w-4 mr-2" />
                    View Feedback
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Chat Interface */}
        <div className="lg:col-span-3">
          <Card className="h-[600px] flex flex-col">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <MessageCircle className="h-5 w-5" />
                Virtual AI Interview
                {interview.status === 'active' && (
                  <Badge variant="default" className="bg-green-500">Live</Badge>
                )}
                {interview.status === 'completed' && (
                  <Badge variant="outline">Completed</Badge>
                )}
              </CardTitle>
            </CardHeader>

            <CardContent className="flex-1 flex flex-col p-0">
              {/* Messages Area */}
              <ScrollArea className="flex-1 p-6">
                <div className="space-y-4">
                  {messages.map((message, index) => (
                    <div key={message.id} className={`flex gap-3 ${message.sender === 'candidate' ? 'flex-row-reverse' : ''}`}>
                      <div className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center ${
                        message.sender === 'interviewer' ? 'bg-blue-100' : 'bg-green-100'
                      }`}>
                        {getMessageIcon(message.sender, message.messageType)}
                      </div>

                      <div className={`flex-1 max-w-[80%] ${message.sender === 'candidate' ? 'text-right' : ''}`}>
                        <div className={`inline-block p-3 rounded-lg ${
                          message.sender === 'interviewer' 
                            ? 'bg-gray-100 text-gray-900' 
                            : 'bg-blue-500 text-white'
                        }`}>
                          <p className="whitespace-pre-wrap">{message.content}</p>

                          {message.messageType === 'question' && (
                            <div className="mt-2 pt-2 border-t border-gray-200">
                              <Badge variant="outline" className="text-xs">
                                Question {index + 1}
                              </Badge>
                            </div>
                          )}
                        </div>

                        {/* Response Quality Indicators */}
                        {message.sender === 'candidate' && (message.responseQuality || message.technicalAccuracy || message.clarityScore) && (
                          <div className="mt-2 flex gap-2 justify-end">
                            {message.responseQuality && getScoreBadge(message.responseQuality * 10)}
                            {message.sentiment && (
                              <Badge variant={message.sentiment === 'positive' ? 'default' : message.sentiment === 'negative' ? 'destructive' : 'secondary'}>
                                {message.sentiment}
                              </Badge>
                            )}
                          </div>
                        )}

                        <p className="text-xs text-gray-500 mt-1">
                          {new Date(message.timestamp).toLocaleTimeString()}
                        </p>
                      </div>
                    </div>
                  ))}

                  {isTyping && (
                    <div className="flex gap-3">
                      <div className="flex-shrink-0 w-8 h-8 rounded-full bg-blue-100 flex items-center justify-center">
                        <Bot className="h-4 w-4 text-blue-500" />
                      </div>
                      <div className="bg-gray-100 p-3 rounded-lg">
                        <div className="flex gap-1">
                          <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                          <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                          <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                        </div>
                      </div>
                    </div>
                  )}

                  <div ref={messagesEndRef} />
                </div>
              </ScrollArea>

              <Separator />

              {/* Input Area - Show as long as interview is not explicitly completed and user can still answer */}
              {interview && (interview.status === 'active' || interview.status === 'paused' || (interview.questionsAsked <= interview.totalQuestions && interview.status !== 'completed')) && (
                <div className="p-4">
                  <div className="flex gap-2">
                    <Input
                      ref={inputRef}
                      value={currentMessage}
                      onChange={(e) => setCurrentMessage(e.target.value)}
                      onKeyPress={handleKeyPress}
                      placeholder="Type your response here..."
                      disabled={isTyping || sendMessageMutation.isPending}
                      className="flex-1"
                    />
                    <Button 
                      onClick={handleSendMessage}
                      disabled={!currentMessage.trim() || isTyping || sendMessageMutation.isPending}
                    >
                      <Send className="h-4 w-4" />
                    </Button>
                  </div>

                  <div className="flex justify-between items-center mt-2 text-xs text-gray-500">
                    <span>Press Enter to send â€¢ Shift+Enter for new line</span>
                    {sendMessageMutation.isPending && <span>Sending...</span>}
                  </div>
                </div>
              )}

              {interview && interview.status === 'completed' && (
                <div className="p-4 bg-gray-50 text-center">
                  <p className="text-gray-600 mb-2">Interview completed!</p>
                  <Button onClick={() => setLocation(`/virtual-interview/${sessionId}/feedback`)}>
                    View Detailed Feedback
                  </Button>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/VirtualInterviewFeedbac.content:
import { useParams, useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Separator } from "@/components/ui/separator";
import { 
  TrendingUp, 
  Award, 
  Target, 
  BookOpen, 
  ArrowLeft,
  CheckCircle,
  AlertCircle,
  Brain,
  MessageCircle,
  Zap,
  BarChart3,
  Star,
  Lightbulb,
  ExternalLink
} from "lucide-react";

interface FeedbackData {
  interview: {
    id: number;
    sessionId: string;
    interviewType: string;
    role: string;
    company?: string;
    difficulty: string;
    duration: number;
    overallScore?: number;
    technicalScore?: number;
    communicationScore?: number;
    confidenceScore?: number;
    strengths: string[];
    weaknesses: string[];
    recommendations: string[];
    detailedFeedback: string;
  };
  feedback: {
    performanceSummary: string;
    keyStrengths: string[];
    areasForImprovement: string[];
    technicalSkillsScore: number;
    problemSolvingScore: number;
    communicationScore: number;
    responseConsistency: number;
    adaptabilityScore: number;
    stressHandling: number;
    recommendedResources: Array<{
      title: string;
      url: string;
      description: string;
    }>;
    nextSteps: string[];
    roleReadiness: 'ready' | 'needs_practice' | 'significant_gaps';
  };
}

export default function VirtualInterviewFeedback() {
  const { sessionId } = useParams<{ sessionId: string }>();
  const [, setLocation] = useLocation();

  const { data, isLoading, error } = useQuery<FeedbackData>({
    queryKey: ['/api/virtual-interview', sessionId, 'feedback'],
    enabled: !!sessionId,
  });

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex items-center justify-center min-h-[400px]">
          <div className="text-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-500 mx-auto"></div>
            <p className="mt-4 text-lg">Generating your detailed feedback...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error || !data) {
    return (
      <div className="container mx-auto px-4 py-8">
        <Card>
          <CardContent className="pt-6">
            <div className="text-center">
              <AlertCircle className="h-16 w-16 text-red-500 mx-auto mb-4" />
              <h2 className="text-2xl font-bold mb-2">Feedback Not Available</h2>
              <p className="text-gray-600 mb-4">We couldn't load the feedback for this interview session.</p>
              <Button onClick={() => setLocation('/virtual-interview/new')}>
                Start New Interview
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  const { interview, feedback } = data;

  const getScoreColor = (score: number) => {
    if (score >= 80) return "text-green-500";
    if (score >= 60) return "text-yellow-500";
    return "text-red-500";
  };

  const getScoreBg = (score: number) => {
    if (score >= 80) return "bg-green-500";
    if (score >= 60) return "bg-yellow-500";
    return "bg-red-500";
  };

  const getReadinessBadge = (readiness: string) => {
    switch (readiness) {
      case 'ready':
        return <Badge className="bg-green-500">Interview Ready</Badge>;
      case 'needs_practice':
        return <Badge className="bg-yellow-500">Needs Practice</Badge>;
      case 'significant_gaps':
        return <Badge variant="destructive">Significant Gaps</Badge>;
      default:
        return <Badge variant="outline">Assessment Pending</Badge>;
    }
  };

  return (
    <div className="container mx-auto px-4 py-6 max-w-6xl">
      {/* Header */}
      <div className="flex items-center gap-4 mb-6">
        <Button 
          variant="outline" 
          onClick={() => setLocation('/virtual-interview/new')}
          className="flex items-center gap-2"
        >
          <ArrowLeft className="h-4 w-4" />
          New Interview
        </Button>
        <div>
          <h1 className="text-3xl font-bold">Interview Feedback</h1>
          <p className="text-gray-600">
            {interview.role.replace(/_/g, ' ')} â€¢ {interview.interviewType} â€¢ {interview.difficulty}
            {interview.company && ` â€¢ ${interview.company}`}
          </p>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">

        {/* Overall Performance */}
        <div className="lg:col-span-2 space-y-6">

          {/* Performance Summary */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Award className="h-5 w-5" />
                Performance Summary
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center gap-4 mb-4">
                <div className="text-center">
                  <div className={`text-4xl font-bold ${getScoreColor(interview.overallScore || 0)}`}>
                    {interview.overallScore || 0}%
                  </div>
                  <p className="text-sm text-gray-600">Overall Score</p>
                </div>
                <div className="flex-1">
                  {getReadinessBadge(feedback.roleReadiness)}
                </div>
              </div>

              <p className="text-gray-700 leading-relaxed">
                {feedback.performanceSummary}
              </p>

              {interview.detailedFeedback && (
                <div className="mt-4 p-4 bg-blue-50 rounded-lg">
                  <p className="text-blue-900">{interview.detailedFeedback}</p>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Detailed Scores */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <BarChart3 className="h-5 w-5" />
                Detailed Performance Breakdown
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">

              {/* Technical Skills */}
              <div>
                <div className="flex justify-between items-center mb-2">
                  <span className="font-medium">Technical Skills</span>
                  <span className={`font-bold ${getScoreColor(feedback.technicalSkillsScore)}`}>
                    {feedback.technicalSkillsScore}%
                  </span>
                </div>
                <Progress value={feedback.technicalSkillsScore} className="h-2" />
              </div>

              {/* Problem Solving */}
              <div>
                <div className="flex justify-between items-center mb-2">
                  <span className="font-medium">Problem Solving</span>
                  <span className={`font-bold ${getScoreColor(feedback.problemSolvingScore)}`}>
                    {feedback.problemSolvingScore}%
                  </span>
                </div>
                <Progress value={feedback.problemSolvingScore} className="h-2" />
              </div>

              {/* Communication */}
              <div>
                <div className="flex justify-between items-center mb-2">
                  <span className="font-medium">Communication</span>
                  <span className={`font-bold ${getScoreColor(feedback.communicationScore)}`}>
                    {feedback.communicationScore}%
                  </span>
                </div>
                <Progress value={feedback.communicationScore} className="h-2" />
              </div>

              {/* Response Consistency */}
              <div>
                <div className="flex justify-between items-center mb-2">
                  <span className="font-medium">Response Consistency</span>
                  <span className={`font-bold ${getScoreColor(feedback.responseConsistency)}`}>
                    {feedback.responseConsistency}%
                  </span>
                </div>
                <Progress value={feedback.responseConsistency} className="h-2" />
              </div>

              {/* Adaptability */}
              <div>
                <div className="flex justify-between items-center mb-2">
                  <span className="font-medium">Adaptability</span>
                  <span className={`font-bold ${getScoreColor(feedback.adaptabilityScore)}`}>
                    {feedback.adaptabilityScore}%
                  </span>
                </div>
                <Progress value={feedback.adaptabilityScore} className="h-2" />
              </div>

              {/* Stress Handling */}
              <div>
                <div className="flex justify-between items-center mb-2">
                  <span className="font-medium">Stress Handling</span>
                  <span className={`font-bold ${getScoreColor(feedback.stressHandling)}`}>
                    {feedback.stressHandling}%
                  </span>
                </div>
                <Progress value={feedback.stressHandling} className="h-2" />
              </div>
            </CardContent>
          </Card>

          {/* Strengths and Weaknesses */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">

            {/* Strengths */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2 text-green-600">
                  <CheckCircle className="h-5 w-5" />
                  Key Strengths
                </CardTitle>
              </CardHeader>
              <CardContent>
                <ul className="space-y-2">
                  {feedback.keyStrengths.map((strength, index) => (
                    <li key={index} className="flex items-start gap-2">
                      <Star className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
                      <span className="text-sm">{strength}</span>
                    </li>
                  ))}
                </ul>
              </CardContent>
            </Card>

            {/* Areas for Improvement */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2 text-orange-600">
                  <Target className="h-5 w-5" />
                  Areas for Improvement
                </CardTitle>
              </CardHeader>
              <CardContent>
                <ul className="space-y-2">
                  {feedback.areasForImprovement.map((area, index) => (
                    <li key={index} className="flex items-start gap-2">
                      <TrendingUp className="h-4 w-4 text-orange-500 mt-0.5 flex-shrink-0" />
                      <span className="text-sm">{area}</span>
                    </li>
                  ))}
                </ul>
              </CardContent>
            </Card>
          </div>

          {/* Next Steps */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Lightbulb className="h-5 w-5" />
                Recommended Next Steps
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {feedback.nextSteps.map((step, index) => (
                  <div key={index} className="flex items-start gap-3 p-3 border rounded-lg">
                    <div className={`w-6 h-6 rounded-full ${getScoreBg(85)} text-white text-xs flex items-center justify-center flex-shrink-0`}>
                      {index + 1}
                    </div>
                    <p className="text-sm">{step}</p>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">

          {/* Quick Stats */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Zap className="h-5 w-5" />
                Quick Stats
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="text-center">
                <p className="text-2xl font-bold text-blue-500">{interview.duration}</p>
                <p className="text-sm text-gray-600">Minutes</p>
              </div>

              <Separator />

              <div className="space-y-2">
                <div className="flex justify-between">
                  <span className="text-sm">Interview Type</span>
                  <Badge variant="outline">{interview.interviewType}</Badge>
                </div>
                <div className="flex justify-between">
                  <span className="text-sm">Difficulty</span>
                  <Badge variant={interview.difficulty === 'hard' ? 'destructive' : interview.difficulty === 'medium' ? 'default' : 'secondary'}>
                    {interview.difficulty}
                  </Badge>
                </div>
                <div className="flex justify-between">
                  <span className="text-sm">Role</span>
                  <span className="text-sm font-medium">{interview.role.replace(/_/g, ' ')}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Learning Resources */}
          {feedback.recommendedResources.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <BookOpen className="h-5 w-5" />
                  Recommended Resources
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                {feedback.recommendedResources.map((resource, index) => (
                  <div key={index} className="p-3 border rounded-lg">
                    <h4 className="font-semibold text-sm mb-1">{resource.title}</h4>
                    <p className="text-xs text-gray-600 mb-2">{resource.description}</p>
                    <Button size="sm" variant="outline" className="h-8 text-xs">
                      <ExternalLink className="h-3 w-3 mr-1" />
                      Learn More
                    </Button>
                  </div>
                ))}
              </CardContent>
            </Card>
          )}

          {/* Actions */}
          <Card>
            <CardContent className="pt-6 space-y-3">
              <Button 
                className="w-full"
                onClick={() => setLocation('/virtual-interview/new')}
              >
                <Brain className="h-4 w-4 mr-2" />
                Practice Again
              </Button>

              <Button 
                variant="outline" 
                className="w-full"
                onClick={() => setLocation(`/virtual-interview/${sessionId}`)}
              >
                <MessageCircle className="h-4 w-4 mr-2" />
                Review Conversation
              </Button>

              <Button 
                variant="outline" 
                className="w-full"
                onClick={() => setLocation('/virtual-interview/history')}
              >
                <Award className="h-4 w-4 mr-2" />
                View History
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/VirtualInterviewStart.t.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { 
  Bot, 
  Briefcase, 
  Clock, 
  Settings, 
  Zap,
  Brain,
  MessageCircle,
  Target,
  TrendingUp,
  Award,
  Play,
  Lock,
  CreditCard,
  AlertCircle
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import PayPalInterviewPayment from "@/components/PayPalInterviewPayment";

export default function VirtualInterviewStart() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [showPaymentDialog, setShowPaymentDialog] = useState(false);

  const [formData, setFormData] = useState({
    interviewType: 'technical',
    role: 'software_engineer',
    company: '',
    difficulty: 'medium',
    duration: 30,
    personality: 'professional',
    style: 'conversational',
    jobDescription: ''
  });

  // Check usage limits
  const { data: usageInfo, refetch: refetchUsage } = useQuery({
    queryKey: ['/api/virtual-interview/usage'],
    queryFn: async () => {
      const response = await apiRequest('GET', '/api/virtual-interview/usage');
      return response.json();
    },
  });

  const startInterviewMutation = useMutation({
    mutationFn: async (data: typeof formData & { isPaid?: boolean; paymentVerificationId?: string }) => {
      const response = await apiRequest('POST', '/api/virtual-interview/start', data);
      if (response.status === 402) {
        const errorData = await response.json();
        throw new Error(JSON.stringify({ requiresPayment: true, ...errorData }));
      }
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Interview Started!",
        description: "Your virtual AI interviewer is ready.",
      });
      setLocation(`/virtual-interview/${data.interview.sessionId}`);
      refetchUsage(); // Refresh usage info
    },
    onError: (error: any) => {
      try {
        const errorData = JSON.parse(error.message);
        if (errorData.requiresPayment) {
          setShowPaymentDialog(true);
          return;
        }
      } catch {}

      toast({
        title: "Error",
        description: error.message || "Failed to start interview session",
        variant: "destructive",
      });
    },
  });

  const handleStart = () => {
    // Check usage limits first - STRICT ENFORCEMENT
    if (usageInfo && usageInfo.requiresPayment) {
      setShowPaymentDialog(true);
      return;
    }

    // Only allow if user has explicit permission and no payment required
    if (usageInfo && usageInfo.canStartInterview && !usageInfo.requiresPayment) {
      startInterviewMutation.mutate(formData);
    } else {
      setShowPaymentDialog(true);
    }
  };

  const handlePaymentComplete = (paymentVerificationId: string) => {
    startInterviewMutation.mutate({ 
      ...formData, 
      isPaid: true, 
      paymentVerificationId 
    });
    setShowPaymentDialog(false);
  };

  const handlePaymentCancel = () => {
    setShowPaymentDialog(false);
  };

  const interviewTypes = [
    { value: 'technical', label: 'Technical Interview', icon: 'ðŸ’»', description: 'Coding, algorithms, system design' },
    { value: 'behavioral', label: 'Behavioral Interview', icon: 'ðŸ¤', description: 'Teamwork, leadership, experiences' },
    { value: 'mixed', label: 'Mixed Interview', icon: 'ðŸŽ¯', description: 'Combination of technical and behavioral' },
    { value: 'system_design', label: 'System Design', icon: 'ðŸ—ï¸', description: 'Architecture and scalability' }
  ];

  const roles = [
    'software_engineer', 'frontend_developer', 'backend_developer', 'fullstack_developer',
    'data_scientist', 'product_manager', 'ui_ux_designer', 'devops_engineer',
    'mobile_developer', 'machine_learning_engineer', 'senior_software_engineer',
    'tech_lead', 'engineering_manager'
  ];

  const personalities = [
    { value: 'friendly', label: 'Friendly & Encouraging', description: 'Supportive and warm interviewer' },
    { value: 'professional', label: 'Professional & Structured', description: 'Business-like and organized' },
    { value: 'challenging', label: 'Challenging & Probing', description: 'Pushes you to think deeper' }
  ];

  const difficulties = [
    { value: 'easy', label: 'Entry Level', color: 'bg-green-500' },
    { value: 'medium', label: 'Mid Level', color: 'bg-yellow-500' },
    { value: 'hard', label: 'Senior Level', color: 'bg-red-500' }
  ];

  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl">
      <div className="text-center mb-8">
        <div className="flex items-center justify-center gap-3 mb-4">
          <div className="p-3 bg-blue-100 rounded-full">
            <Bot className="h-8 w-8 text-blue-600" />
          </div>
          <h1 className="text-4xl font-bold">Real Interview Simulation</h1>
        </div>
        <p className="text-xl text-gray-600 max-w-2xl mx-auto">
          Experience realistic interview conversations with our AI interviewer. Get real-time feedback, 
          improve your communication skills, and build confidence for actual interviews.
        </p>

        {/* Usage Information */}
        {usageInfo && (
          <div className="mt-6 max-w-md mx-auto">
            <Card className="border-blue-200 bg-blue-50">
              <CardContent className="pt-6">
                <div className="text-center">
                  <h3 className="font-semibold text-blue-800 mb-2">Interview Usage</h3>
                  {usageInfo.freeInterviewsRemaining > 0 && (
                    <p className="text-sm text-blue-700">
                      ðŸŽ‰ {usageInfo.freeInterviewsRemaining} free interview{usageInfo.freeInterviewsRemaining === 1 ? '' : 's'} remaining
                    </p>
                  )}
                  {usageInfo.monthlyInterviewsRemaining > 0 && (
                    <p className="text-sm text-blue-700">
                      â­ {usageInfo.monthlyInterviewsRemaining} premium interview{usageInfo.monthlyInterviewsRemaining === 1 ? '' : 's'} remaining this month
                    </p>
                  )}
                  {usageInfo.requiresPayment && (
                    <p className="text-sm text-orange-700 flex items-center justify-center gap-2">
                      <CreditCard className="h-4 w-4" />
                      Next interview: ${usageInfo.cost || 2}
                    </p>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>
        )}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Configuration Form */}
        <div className="lg:col-span-2">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Settings className="h-5 w-5" />
                Interview Configuration
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">

              {/* Interview Type */}
              <div>
                <Label className="text-sm font-medium mb-3 block">Interview Type</Label>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                  {interviewTypes.map((type) => (
                    <div
                      key={type.value}
                      className={`p-4 border rounded-lg cursor-pointer transition-colors ${
                        formData.interviewType === type.value
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }`}
                      onClick={() => setFormData({ ...formData, interviewType: type.value })}
                    >
                      <div className="flex items-start gap-3">
                        <span className="text-2xl">{type.icon}</span>
                        <div>
                          <h3 className="font-semibold">{type.label}</h3>
                          <p className="text-sm text-gray-600">{type.description}</p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Role and Company */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="role">Role</Label>
                  <Select value={formData.role} onValueChange={(value) => setFormData({ ...formData, role: value })}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select role" />
                    </SelectTrigger>
                    <SelectContent>
                      {roles.map((role) => (
                        <SelectItem key={role} value={role}>
                          {role.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="company">Company (Optional)</Label>
                  <Input
                    id="company"
                    value={formData.company}
                    onChange={(e) => setFormData({ ...formData, company: e.target.value })}
                    placeholder="e.g., Google, Microsoft"
                  />
                </div>
              </div>

              {/* Difficulty */}
              <div>
                <Label className="text-sm font-medium mb-3 block">Difficulty Level</Label>
                <div className="grid grid-cols-3 gap-3">
                  {difficulties.map((diff) => (
                    <div
                      key={diff.value}
                      className={`p-3 border rounded-lg cursor-pointer transition-colors text-center ${
                        formData.difficulty === diff.value
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }`}
                      onClick={() => setFormData({ ...formData, difficulty: diff.value })}
                    >
                      <div className={`w-4 h-4 rounded-full ${diff.color} mx-auto mb-2`}></div>
                      <p className="font-semibold">{diff.label}</p>
                    </div>
                  ))}
                </div>
              </div>

              {/* Duration */}
              <div>
                <Label className="text-sm font-medium mb-3 block">
                  Duration: {formData.duration} minutes
                </Label>
                <Slider
                  value={[formData.duration]}
                  onValueChange={(value) => setFormData({ ...formData, duration: value[0] })}
                  max={60}
                  min={10}
                  step={5}
                  className="w-full"
                />
                <div className="flex justify-between text-xs text-gray-500 mt-1">
                  <span>10 min</span>
                  <span>30 min</span>
                  <span>60 min</span>
                </div>
              </div>

              {/* Interviewer Personality */}
              <div>
                <Label className="text-sm font-medium mb-3 block">Interviewer Personality</Label>
                <div className="space-y-2">
                  {personalities.map((personality) => (
                    <div
                      key={personality.value}
                      className={`p-3 border rounded-lg cursor-pointer transition-colors ${
                        formData.personality === personality.value
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }`}
                      onClick={() => setFormData({ ...formData, personality: personality.value })}
                    >
                      <h3 className="font-semibold">{personality.label}</h3>
                      <p className="text-sm text-gray-600">{personality.description}</p>
                    </div>
                  ))}
                </div>
              </div>

              {/* Job Description */}
              <div>
                <Label htmlFor="jobDescription">Job Description (Optional)</Label>
                <Textarea
                  id="jobDescription"
                  value={formData.jobDescription}
                  onChange={(e) => setFormData({ ...formData, jobDescription: e.target.value })}
                  placeholder="Paste the job description here for more tailored questions..."
                  rows={4}
                />
              </div>

              <Button 
                onClick={handleStart}
                disabled={startInterviewMutation.isPending}
                className="w-full h-12 text-lg"
              >
                {startInterviewMutation.isPending ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Starting Interview...
                  </>
                ) : (
                  <>
                    <Play className="h-5 w-5 mr-2" />
                    Start Virtual Interview
                  </>
                )}
              </Button>
            </CardContent>
          </Card>
        </div>

        {/* Benefits Sidebar */}
        <div className="lg:col-span-1">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Award className="h-5 w-5" />
                What You'll Get
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex items-start gap-3">
                <Brain className="h-5 w-5 text-blue-500 mt-1" />
                <div>
                  <h3 className="font-semibold">AI-Powered Analysis</h3>
                  <p className="text-sm text-gray-600">Real-time assessment of your responses</p>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <MessageCircle className="h-5 w-5 text-green-500 mt-1" />
                <div>
                  <h3 className="font-semibold">Natural Conversation</h3>
                  <p className="text-sm text-gray-600">Chat-based interface like real interviews</p>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <Target className="h-5 w-5 text-purple-500 mt-1" />
                <div>
                  <h3 className="font-semibold">Personalized Questions</h3>
                  <p className="text-sm text-gray-600">Tailored to your role and experience</p>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <TrendingUp className="h-5 w-5 text-orange-500 mt-1" />
                <div>
                  <h3 className="font-semibold">Detailed Feedback</h3>
                  <p className="text-sm text-gray-600">Comprehensive analysis and improvement tips</p>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <Zap className="h-5 w-5 text-yellow-500 mt-1" />
                <div>
                  <h3 className="font-semibold">Instant Results</h3>
                  <p className="text-sm text-gray-600">Get feedback immediately after completion</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="mt-6">
            <CardContent className="pt-6">
              <div className="text-center">
                <Clock className="h-8 w-8 text-blue-500 mx-auto mb-2" />
                <h3 className="font-semibold mb-2">Quick Practice Session</h3>
                <p className="text-sm text-gray-600 mb-4">
                  Perfect for last-minute interview preparation or skill building
                </p>
                <div className="grid grid-cols-3 gap-2 text-center">
                  <div>
                    <p className="text-2xl font-bold text-blue-500">5+</p>
                    <p className="text-xs text-gray-600">Questions</p>
                  </div>
                  <div>
                    <p className="text-2xl font-bold text-green-500">Live</p>
                    <p className="text-xs text-gray-600">Feedback</p>
                  </div>
                  <div>
                    <p className="text-2xl font-bold text-purple-500">AI</p>
                    <p className="text-xs text-gray-600">Powered</p>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Payment Dialog */}
      <Dialog open={showPaymentDialog} onOpenChange={setShowPaymentDialog}>
        <DialogContent className="sm:max-w-lg">
          <PayPalInterviewPayment 
            cost={usageInfo?.cost || 5}
            onPaymentComplete={handlePaymentComplete}
            onCancel={handlePaymentCancel}
            isProcessing={startInterviewMutation.isPending}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
}

