                  <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                    Select a job to view details
                  </h3>
                  <p className="text-gray-600 dark:text-gray-300">
                    Click on any job from the list to see more information
                  </p>
                </CardContent>
              </Card>
            )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

