                                            {jobCompatibility.missingSkills && (
                                              <div>
                                                <Label className="text-sm font-medium text-orange-700">
                                                  Skills to Develop
                                                </Label>
                                                <div className="flex flex-wrap gap-1 mt-1">
                                                  {jobCompatibility.missingSkills
                                                    .slice(0, 5)
                                                    .map(
                                                      (
                                                        skill: string,
                                                        index: number,
                                                      ) => (
                                                        <Badge
                                                          key={index}
                                                          variant="secondary"
                                                          className="bg-orange-100 text-orange-700 text-xs"
                                                        >
                                                          {skill}
                                                        </Badge>
                                                      ),
                                                    )}
                                                </div>
                                              </div>
                                            )}

                                            {jobCompatibility.applicationRecommendation && (
                                              <div>
                                                <Label className="text-sm font-medium">
                                                  AI Recommendation
                                                </Label>
                                                <p className="text-sm text-gray-700 mt-1 p-3 bg-gray-50 rounded-md">
                                                  {
                                                    jobCompatibility.applicationRecommendation
                                                  }
                                                </p>
                                              </div>
                                            )}
                                          </div>
                                        ) : (
                                          <div className="text-center py-4 text-gray-500">
                                            <p>
                                              Click "Analyze Fit" to get
                                              AI-powered compatibility analysis
                                            </p>
                                          </div>
                                        )}
                                      </CardContent>
                                    </Card>
                                  </div>
                                ) : (
                                  <div className="text-center py-8">
                                    <p className="text-gray-600">
                                      Failed to load candidate details
                                    </p>
                                  </div>
                                )}
                              </DialogContent>
                            </Dialog>

                            <Dialog
                              open={selectedApplication?.id === application.id}
                              onOpenChange={(open) => {
                                if (!open) {
                                  setSelectedApplication(null);
                                }
                              }}
                            >
                              <DialogTrigger asChild>
                                <Button
                                  variant="outline"
                                  size="sm"
                                  onClick={() => {
                                    setSelectedApplication(application);
                                    setApplicationStatus(
                                      application.status || "pending",
                                    );
                                    setRecruiterNotes(
                                      application.recruiterNotes || "",
                                    );
                                  }}
                                >
                                  <CheckCircle className="w-4 h-4 mr-1" />
                                  Review
                                </Button>
                              </DialogTrigger>
                              <DialogContent>
                                <DialogHeader>
                                  <DialogTitle>Review Application</DialogTitle>
                                  <DialogDescription>
                                    Update the status and add notes for this
                                    application
                                  </DialogDescription>
                                </DialogHeader>
                                <div className="space-y-4">
                                  <div>
                                    <Label htmlFor="status">
                                      Application Status
                                    </Label>
                                    <Select
                                      value={applicationStatus}
                                      onValueChange={setApplicationStatus}
                                    >
                                      <SelectTrigger>
                                        <SelectValue />
                                      </SelectTrigger>
                                      <SelectContent>
                                        <SelectItem value="pending">
                                          Pending Review
                                        </SelectItem>
                                        <SelectItem value="reviewed">
                                          Reviewed
                                        </SelectItem>
                                        <SelectItem value="shortlisted">
                                          Shortlisted
                                        </SelectItem>
                                        <SelectItem value="interviewed">
                                          Interviewed
                                        </SelectItem>
                                        <SelectItem value="hired">
                                          Hired
                                        </SelectItem>
                                        <SelectItem value="rejected">
                                          Rejected
                                        </SelectItem>
                                      </SelectContent>
                                    </Select>
                                  </div>

                                  <div>
                                    <Label htmlFor="notes">
                                      Recruiter Notes
                                    </Label>
                                    <Textarea
                                      id="notes"
                                      placeholder="Add your notes about this candidate..."
                                      value={recruiterNotes}
                                      onChange={(e) =>
                                        setRecruiterNotes(e.target.value)
                                      }
                                      rows={3}
                                    />
                                  </div>

                                  <div className="flex justify-end gap-2">
                                    <Button
                                      variant="outline"
                                      onClick={() =>
                                        setSelectedApplication(null)
                                      }
                                    >
                                      Cancel
                                    </Button>
                                    <Button
                                      onClick={() =>
                                        handleUpdateApplication(
                                          applicationStatus,
                                        )
                                      }
                                      disabled={
                                        updateApplicationMutation.isPending
                                      }
                                    >
                                      {updateApplicationMutation.isPending
                                        ? "Updating..."
                                        : "Update Status"}
                                    </Button>
                                  </div>
                                </div>
                              </DialogContent>
                            </Dialog>

                            {/* Message Button */}
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => {
                                setLocation(`/chat?user=${application.applicantId}`);
                              }}
                            >
                              <MessageCircle className="w-4 h-4 mr-1" />
                              Message
                            </Button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* Interviews Tab */}
          <TabsContent value="interviews">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
